"use client";

import { Search, ChevronRight } from "lucide-react";
import {
  LayoutDashboard,
  ShoppingBag,
  Receipt,
  BarChart3,
  MessageSquare,
  Users2,
  Megaphone,
  UserCircle,
  MessageCircle,
  ClipboardList,
  Shield,
  CreditCard,
  Puzzle,
  Settings,
  GalleryVerticalEnd,
  AudioWaveform,
  Command,
} from "lucide-react";
import { Input } from "@/components/ui/input";
import {
  Sidebar,
  StatsCard,
  SkeletonCard,
  type Team,
  type User,
  type NavSection,
} from "./components";
import { SidebarProvider } from "@/components/ui/sidebar";
import { LogoIcon } from "../social-icons/icons";

// =============================================================================
// DATA
// =============================================================================

const teams: Team[] = [
  {
    name: "ShadcnShip",
    plan: "Enterprise",
    logo: <LogoIcon className="size-4 invert" />,
  },
  {
    name: "Acme Corp.",
    plan: "Startup",
    logo: <AudioWaveform className="size-4" />,
  },
  {
    name: "Evil Corp.",
    plan: "Free",
    logo: <Command className="size-4" />,
  },
];

const user: User = {
  name: "shadcn",
  email: "m@example.com",
  avatar: "/avatars/shadcn.jpg",
};

const navigation: NavSection[] = [
  {
    title: "Main Menu",
    items: [
      {
        icon: <LayoutDashboard className="size-4" />,
        label: "Dashboard",
        active: true,
      },
      { icon: <ShoppingBag className="size-4" />, label: "Products" },
      { icon: <Receipt className="size-4" />, label: "Transactions" },
      { icon: <BarChart3 className="size-4" />, label: "Reports & Analytics" },
      { icon: <MessageSquare className="size-4" />, label: "Messages" },
      { icon: <Users2 className="size-4" />, label: "Team Performance" },
      { icon: <Megaphone className="size-4" />, label: "Campaigns" },
    ],
  },
  {
    title: "Customers",
    items: [
      { icon: <UserCircle className="size-4" />, label: "Customer List" },
      { icon: <MessageCircle className="size-4" />, label: "Channels" },
      { icon: <ClipboardList className="size-4" />, label: "Order Management" },
    ],
  },
  {
    title: "Management",
    items: [
      { icon: <Shield className="size-4" />, label: "Roles & Permissions" },
      {
        icon: <CreditCard className="size-4" />,
        label: "Billing & Subscription",
      },
      { icon: <Puzzle className="size-4" />, label: "Integrations" },
    ],
  },
];

// =============================================================================
// MAIN COMPONENT
// =============================================================================

export default function Dashboard() {
  return (
    <div className="flex h-screen bg-background w-full">
      <SidebarProvider>
        {/* Sidebar */}
        <Sidebar teams={teams} user={user} navigation={navigation} />

        {/* Main Content */}
        <main className="flex-1 overflow-y-auto">
          {/* Header */}
          <header className="flex items-center justify-between border-b px-6 py-4">
            <div className="flex items-center gap-2 text-sm">
              <span className="text-muted-foreground">Dashboard</span>
              <ChevronRight className="size-4 text-muted-foreground" />
              <span className="font-medium">Overview</span>
            </div>
            <div className="relative w-64">
              <Search className="absolute left-3 top-1/2 size-4 -translate-y-1/2 text-muted-foreground" />
              <Input
                type="text"
                placeholder="Search..."
                className="pl-9 bg-muted/50 border-0"
              />
            </div>
          </header>

          {/* Content */}
          <div className="p-6 space-y-6">
            {/* Welcome message */}
            <h1 className="text-3xl font-semibold tracking-tight">
              Welcome back Shadcn,
            </h1>

            {/* Stats cards */}
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
              <StatsCard />
              <StatsCard />
              <StatsCard />
            </div>

            {/* Sales Trend */}
            <SkeletonCard />
          </div>
        </main>
      </SidebarProvider>
    </div>
  );
}

export { NavUser, type User } from "./nav-user";
export { TeamSwitcher, type Team } from "./team-switcher";
export { Sidebar, type NavItem, type NavSection } from "./sidebar";
export { StatsCard } from "./skeleton-card-stat";
export { SkeletonCard } from "./skeleton-card";

"use client";

import { cn } from "@/lib/utils";
import { NavUser, type User } from "./nav-user";
import { TeamSwitcher, type Team } from "./team-switcher";
import {
  SidebarFooter,
  SidebarGroup,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarContent,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarMenuSub,
  SidebarMenuSubButton,
  SidebarMenuSubItem,
  SidebarGroupContent,
  SidebarGroupAction,
} from "@/components/ui/sidebar";
export interface NavItem {
  icon: React.ReactNode;
  label: string;
  active?: boolean;
}

export interface NavSection {
  title?: string;
  items: NavItem[];
}

interface SidebarProps {
  teams: Team[];
  user: User;
  navigation: NavSection[];
}

export function Sidebar({ teams, user, navigation }: SidebarProps) {
  return (
    <aside className="flex w-64 flex-col border-r bg-card">
      {/* Team Switcher */}
      <div className="px-4 py-2">
        <TeamSwitcher teams={teams} />
      </div>

      {/* Navigation */}
      <SidebarContent>
        {navigation.map((section, sectionIndex) => (
          <SidebarGroup>
            <SidebarGroupLabel>{section.title}</SidebarGroupLabel>
            <SidebarGroupContent>
              <SidebarMenu>
                {section.items.map((item, itemIndex) => (
                  <SidebarMenuItem key={item.label}>
                    <SidebarMenuButton
                      tooltip={item.label}
                      className="text-muted-foreground"
                    >
                      {item.icon}
                      <span>{item.label}</span>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                ))}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        ))}
      </SidebarContent>

      {/* User Menu - Fixed at bottom */}
      <SidebarFooter>
        <NavUser user={user} />
      </SidebarFooter>
    </aside>
  );
}

"use client";

import * as React from "react";
import { ChevronsUpDown, Plus } from "lucide-react";

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuShortcut,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  useSidebar,
} from "@/components/ui/sidebar";

export interface Team {
  name: string;
  logo: React.ReactNode;
  plan: string;
}

export function TeamSwitcher({ teams }: { teams: Team[] }) {
  const { isMobile } = useSidebar();
  const [activeTeam, setActiveTeam] = React.useState(teams[0]);

  if (!activeTeam) {
    return null;
  }

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <SidebarMenuButton
              size="lg"
              className="data-[state=open]:bg-sidebar-accent data-[state=open]:text-sidebar-accent-foreground"
            >
              <div className="bg-sidebar-primary text-sidebar-primary-foreground flex aspect-square size-8 items-center justify-center rounded-lg">
                {activeTeam.logo}
              </div>
              <div className="grid flex-1 text-left text-sm leading-tight">
                <span className="truncate font-medium">{activeTeam.name}</span>
                <span className="truncate text-xs">{activeTeam.plan}</span>
              </div>
              <ChevronsUpDown className="ml-auto" />
            </SidebarMenuButton>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="w-(--radix-dropdown-menu-trigger-width) min-w-56 rounded-lg"
            align="start"
            side={isMobile ? "bottom" : "right"}
            sideOffset={4}
          >
            <DropdownMenuLabel className="text-muted-foreground text-xs">
              Teams
            </DropdownMenuLabel>
            {teams.map((team, index) => (
              <DropdownMenuItem
                key={team.name}
                onClick={() => setActiveTeam(team)}
                className="gap-2 p-2"
              >
                <div className="flex size-6 items-center justify-center rounded-md border">
                  <div className="size-3.5 shrink-0">{team.logo}</div>
                </div>
                {team.name}
                <DropdownMenuShortcut>âŒ˜{index + 1}</DropdownMenuShortcut>
              </DropdownMenuItem>
            ))}
            <DropdownMenuSeparator />
            <DropdownMenuItem className="gap-2 p-2">
              <div className="flex size-6 items-center justify-center rounded-md border bg-transparent">
                <Plus className="size-4" />
              </div>
              <div className="text-muted-foreground font-medium">Add team</div>
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </SidebarMenuItem>
    </SidebarMenu>
  );
}

"use client";

import { Skeleton } from "@/components/ui/skeleton";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

export function StatsCard() {
  return (
    <Card className="w-full  shadow-none">
      <CardHeader>
        <Skeleton className="h-4 w-2/3" />
        <Skeleton className="h-4 w-1/2" />
      </CardHeader>
    </Card>
  );
}

import { Skeleton } from "@/components/ui/skeleton";
import { Card, CardContent, CardHeader } from "@/components/ui/card";

export function SkeletonCard() {
  return (
    <Card className="w-full shadow-none">
      <CardHeader>
        <Skeleton className="h-4 w-2/3" />
        <Skeleton className="h-4 w-1/2" />
      </CardHeader>
      <CardContent>
        <Skeleton className="aspect-12/4 w-full" />
      </CardContent>
    </Card>
  );
}
