"use client";

import {
  LayoutDashboard,
  FolderKanban,
  CheckSquare,
  Map,
  Users,
  Bell,
  Database,
  Cable,
  FileText,
  ShieldCheck,
  UserCog,
  Lock,
  CreditCard,
  Download,
  Upload,
  BarChart3,
  FileBarChart,
  MessageSquare,
  Command,
} from "lucide-react";
import {
  Sidebar,
  StatCard,
  RevenueSummaryChart,
  UserLocationsCard,
  ProjectsTable,
  type Team,
  type User,
  type NavSection,
} from "./components";
import { SidebarProvider } from "@/components/ui/sidebar";
import { LogoIcon } from "../social-icons/icons";

// =============================================================================
// DATA
// =============================================================================

const teams: Team[] = [
  {
    name: "ShadcnShip",
    plan: "Pro",
    logo: <LogoIcon className="size-5 invert dark:invert-0" />,
  },
];

const user: User = {
  name: "Shadcn",
  email: "shadcn@ship.com",
  avatar: "/avatars/shadcn.jpg",
};

const navigation: NavSection[] = [
  {
    title: "Workspace",
    items: [
      {
        icon: <LayoutDashboard className="size-4" />,
        label: "Overview",
        active: true,
      },
      { icon: <FolderKanban className="size-4" />, label: "Projects" },
      { icon: <CheckSquare className="size-4" />, label: "Tasks" },
      { icon: <Map className="size-4" />, label: "Roadmap" },
      { icon: <Users className="size-4" />, label: "Audience" },
      { icon: <Bell className="size-4" />, label: "Notifications" },
      { icon: <Database className="size-4" />, label: "Database" },
      { icon: <Cable className="size-4" />, label: "Connections" },
      { icon: <FileText className="size-4" />, label: "Documentation" },
    ],
  },
  {
    title: "Ventures",
    items: [
      { icon: <ShieldCheck className="size-4" />, label: "Authentication" },
      { icon: <UserCog className="size-4" />, label: "User Management" },
      { icon: <Lock className="size-4" />, label: "Security" },
      { icon: <CreditCard className="size-4" />, label: "Payments" },
      { icon: <Download className="size-4" />, label: "Import Data" },
      { icon: <Upload className="size-4" />, label: "Export Data" },
    ],
  },
  {
    title: "Insights",
    items: [
      { icon: <BarChart3 className="size-4" />, label: "Analytics" },
      { icon: <FileBarChart className="size-4" />, label: "Reports" },
      { icon: <MessageSquare className="size-4" />, label: "User Feedback" },
    ],
  },
];

// =============================================================================
// MAIN COMPONENT
// =============================================================================

export default function Dashboard() {
  return (
    <div className="flex h-screen bg-background w-full">
      <SidebarProvider>
        {/* Sidebar */}
        <Sidebar teams={teams} user={user} navigation={navigation} />

        {/* Main Content */}
        <main className="flex-1 overflow-y-auto bg-background">
          {/* Header */}
          <header className="px-6 py-4">
            <h1 className="text-xl font-semibold">Good morning, Shadcn</h1>
            <div className="flex gap-4 mt-3">
              {["General", "Members", "Notifications"].map((tab, i) => (
                <button
                  key={tab}
                  className={`text-sm pb-2 border-b-2 transition-colors ${
                    i === 0
                      ? "border-foreground text-foreground font-medium"
                      : "border-transparent text-muted-foreground hover:text-foreground"
                  }`}
                >
                  {tab}
                </button>
              ))}
            </div>
          </header>

          {/* Content */}
          <div className="px-6 pb-6 space-y-4">
            {/* Stats Row */}
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
              <StatCard
                title="Active Users"
                value="2,450"
                change="+18.2%"
                positive
                label="Growing steadily"
                description="Active users in the last 30 days"
              />
              <StatCard
                title="New Subscriptions"
                value="145"
                change="+5.4%"
                positive
                label="Slightly up"
                description="New signups this week"
              />
              <StatCard
                title="Churn Rate"
                value="2.1%"
                change="-4.2%"
                positive
                label="Improving"
                description="Churn rate compared to last month"
              />
              <StatCard
                title="Revenue"
                value="$12,450"
                change="+2.5%"
                positive
                label="Above target"
                description="Total revenue this month"
              />
            </div>

            {/* Charts Row */}
            <div className="grid gap-4 lg:grid-cols-2">
              <UserLocationsCard />
              <RevenueSummaryChart />
            </div>

            {/* Projects Table */}
            <ProjectsTable />
          </div>
        </main>
      </SidebarProvider>
    </div>
  );
}

export { NavUser, type User } from "./nav-user";
export { TeamSwitcher, type Team } from "./team-switcher";
export { Sidebar, type NavItem, type NavSection } from "./sidebar";
export { StatCard } from "./stat-cards";
export { RevenueSummaryChart, RevenueChart, DeliveryTimeChart, AnalyticsChart } from "./analytics-chart";
export { UserLocationsCard } from "./user-locations";
export { ProjectsTable } from "./projects-table";

"use client";

import { NavUser, type User } from "./nav-user";
import { TeamSwitcher, type Team } from "./team-switcher";
import {
  SidebarFooter,
  SidebarGroup,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarContent,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarGroupContent,
} from "@/components/ui/sidebar";

export interface NavItem {
  icon: React.ReactNode;
  label: string;
  active?: boolean;
}

export interface NavSection {
  title?: string;
  items: NavItem[];
}

interface SidebarProps {
  teams: Team[];
  user: User;
  navigation: NavSection[];
}

export function Sidebar({ teams, user, navigation }: SidebarProps) {
  return (
    <aside className="flex w-64 flex-col border-r bg-card">
      {/* Team Switcher */}
      <div className="px-2 py-2">
        <TeamSwitcher teams={teams} />
      </div>

      {/* Navigation */}
      <SidebarContent className="[&::-webkit-scrollbar]:hidden [-ms-overflow-style:none] [scrollbar-width:none]">
        {navigation.map((section) => (
          <SidebarGroup key={section.title}>
            <SidebarGroupLabel>{section.title}</SidebarGroupLabel>
            <SidebarGroupContent>
              <SidebarMenu>
                {section.items.map((item) => (
                  <SidebarMenuItem key={item.label}>
                    <SidebarMenuButton
                      tooltip={item.label}
                      className="text-muted-foreground"
                    >
                      {item.icon}
                      <span>{item.label}</span>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                ))}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        ))}
      </SidebarContent>

      {/* User Menu - Fixed at bottom */}
      <SidebarFooter>
        <NavUser user={user} />
      </SidebarFooter>
    </aside>
  );
}

"use client";

import * as React from "react";
import { ChevronsUpDown, Plus } from "lucide-react";

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuShortcut,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  useSidebar,
} from "@/components/ui/sidebar";

export interface Team {
  name: string;
  logo: React.ReactNode;
  plan: string;
}

export function TeamSwitcher({ teams }: { teams: Team[] }) {
  const { isMobile } = useSidebar();
  const [activeTeam, setActiveTeam] = React.useState(teams[0]);

  if (!activeTeam) {
    return null;
  }

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <SidebarMenuButton
              size="lg"
              className="data-[state=open]:bg-sidebar-accent data-[state=open]:text-sidebar-accent-foreground"
            >
              <div className="bg-primary text-primary-foreground flex aspect-square size-8 items-center justify-center rounded-lg">
                {activeTeam.logo}
              </div>
              <div className="grid flex-1 text-left text-sm leading-tight">
                <span className="truncate font-medium">{activeTeam.name}</span>
                <span className="truncate text-xs">{activeTeam.plan}</span>
              </div>
              <ChevronsUpDown className="ml-auto" />
            </SidebarMenuButton>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="w-(--radix-dropdown-menu-trigger-width) min-w-56 rounded-lg"
            align="start"
            side={isMobile ? "bottom" : "right"}
            sideOffset={4}
          >
            <DropdownMenuLabel className="text-muted-foreground text-xs">
              Teams
            </DropdownMenuLabel>
            {teams.map((team, index) => (
              <DropdownMenuItem
                key={team.name}
                onClick={() => setActiveTeam(team)}
                className="gap-2 p-2"
              >
                <div className="flex size-6 items-center justify-center rounded-md border">
                  <div className="size-3.5 shrink-0">{team.logo}</div>
                </div>
                {team.name}
                <DropdownMenuShortcut>⌘{index + 1}</DropdownMenuShortcut>
              </DropdownMenuItem>
            ))}
            <DropdownMenuSeparator />
            <DropdownMenuItem className="gap-2 p-2">
              <div className="flex size-6 items-center justify-center rounded-md border bg-transparent">
                <Plus className="size-4" />
              </div>
              <div className="text-muted-foreground font-medium">Add team</div>
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </SidebarMenuItem>
    </SidebarMenu>
  );
}

"use client";

import { Badge } from "@/components/ui/badge";
import { Card } from "@/components/ui/card";
import { TrendingUp, TrendingDown } from "lucide-react";

interface StatCardProps {
  title: string;
  value: string;
  change: string;
  positive: boolean;
  label: string;
  description: string;
}

export function StatCard({
  title,
  value,
  change,
  positive,
  label,
  description,
}: StatCardProps) {
  return (
    <Card className="p-4 shadow-none bg-card gap-1">
      <div className="flex items-center justify-between mb-1">
        <span className="text-sm text-muted-foreground">{title}</span>
        <Badge
          variant={"outline"}
          className="text-xs flex items-center gap-0.5"
        >
          {change}
          {positive ? (
            <TrendingUp className="size-3" />
          ) : (
            <TrendingDown className="size-3" />
          )}
        </Badge>
      </div>
      <p className="text-3xl font-bold mb-1">{value}</p>
      <p className="text-sm text-foreground">
        {label} <span className="text-primary">{positive ? "↗" : "↘"}</span>
      </p>
      <p className="text-xs text-muted-foreground mt-0.5">{description}</p>
    </Card>
  );
}

"use client";

import { Card } from "@/components/ui/card";
import {
  ChartConfig,
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
} from "@/components/ui/chart";
import { Bar, BarChart, CartesianGrid, XAxis, YAxis } from "recharts";

const chartData = [
  { month: "Aug", billed: 28, unpaid: 8 },
  { month: "Sep", billed: 32, unpaid: 12 },
  { month: "Oct", billed: 38, unpaid: 10 },
  { month: "Nov", billed: 42, unpaid: 14 },
  { month: "Dec", billed: 48, unpaid: 10 },
  { month: "Jan", billed: 52, unpaid: 8 },
];

const chartConfig = {
  billed: {
    label: "Billed amount",
    color: "hsl(var(--foreground))",
  },
  unpaid: {
    label: "Unpaid amount",
    color: "hsl(var(--muted-foreground))",
  },
} satisfies ChartConfig;

export function RevenueSummaryChart() {
  return (
    <Card className="p-4 shadow-none">
      <div className="flex items-center justify-between mb-1">
        <div>
          <h3 className="font-semibold text-sm">Revenue Summary</h3>
          <p className="text-xs text-muted-foreground">Monthly comparison</p>
        </div>
        <div className="flex items-center gap-4 text-xs">
          <div className="flex items-center gap-1.5">
            <span className="size-2 rounded-full bg-foreground" />
            <span className="text-muted-foreground">Billed amount</span>
          </div>
          <div className="flex items-center gap-1.5">
            <span className="size-2 rounded-full bg-muted-foreground/50" />
            <span className="text-muted-foreground">Unpaid amount</span>
          </div>
        </div>
      </div>

      <ChartContainer config={chartConfig} className="h-fit w-full mt-4">
        <BarChart data={chartData} barGap={2}>
          <CartesianGrid vertical={false} />
          <XAxis
            dataKey="month"
            tickLine={false}
            axisLine={false}
            tickMargin={8}
            tick={{ fontSize: 11 }}
          />
          <YAxis
            tickLine={false}
            axisLine={false}
            tickMargin={8}
            tick={{ fontSize: 11 }}
            tickFormatter={(value) => `$${value}k`}
          />
          <ChartTooltip
            content={
              <ChartTooltipContent
                formatter={(value, name) => (
                  <span className="font-medium">
                    ${value}k {name === "billed" ? "billed" : "unpaid"}
                  </span>
                )}
              />
            }
          />
          <Bar
            dataKey="billed"
            fill="var(--foreground)"
            radius={[4, 4, 0, 0]}
            barSize={24}
          />
          <Bar
            dataKey="unpaid"
            fill="var(--muted)"
            radius={[4, 4, 0, 0]}
            barSize={24}
          />
        </BarChart>
      </ChartContainer>
    </Card>
  );
}

// Keep old exports for compatibility
export { RevenueSummaryChart as RevenueChart };
export { RevenueSummaryChart as DeliveryTimeChart };
export { RevenueSummaryChart as AnalyticsChart };

