"use client";

import {
  LayoutDashboard,
  Users,
  CreditCard,
  BarChart3,
  Settings,
  Bell,
  FileText,
  HelpCircle,
  UserPlus,
  Download,
  Send,
  PlusCircle,
  DollarSign,
  TrendingUp,
  Activity,
} from "lucide-react";
import {
  Sidebar,
  Header,
  StatCard,
  RevenueChart,
  UserGrowthChart,
  RecentActivity,
  QuickActions,
  DataTableWidget,
  type Team,
  type User,
  type NavSection,
  type DashboardData,
  type QuickAction,
  type TableItem,
} from "./components";
import { SidebarProvider } from "@/components/ui/sidebar";
import { LogoIcon } from "../social-icons/icons";

// =============================================================================
// DEFAULT DATA
// =============================================================================

const defaultTeams: Team[] = [
  {
    name: "ShadcnShip",
    plan: "Pro",
    logo: <LogoIcon className="size-5 invert dark:invert-0" />,
  },
];

const defaultUser: User = {
  name: "Shadcn",
  email: "shadcn@ship.com",
  avatar: "/avatars/shadcn.jpg",
};

const defaultNavigation: NavSection[] = [
  {
    title: "Overview",
    items: [
      {
        icon: <LayoutDashboard className="size-4" />,
        label: "Dashboard",
        active: true,
      },
      { icon: <BarChart3 className="size-4" />, label: "Analytics" },
      { icon: <FileText className="size-4" />, label: "Reports" },
    ],
  },
  {
    title: "Management",
    items: [
      { icon: <Users className="size-4" />, label: "Customers", badge: 12 },
      { icon: <CreditCard className="size-4" />, label: "Billing" },
      { icon: <Bell className="size-4" />, label: "Notifications", badge: 3 },
    ],
  },
  {
    title: "Settings",
    items: [
      { icon: <Settings className="size-4" />, label: "Settings" },
      { icon: <HelpCircle className="size-4" />, label: "Help & Support" },
    ],
  },
];

const defaultQuickActions: QuickAction[] = [
  {
    id: "1",
    label: "Add Customer",
    icon: <UserPlus className="size-4" />,
    href: "#",
  },
  {
    id: "2",
    label: "Create Invoice",
    icon: <PlusCircle className="size-4" />,
    href: "#",
  },
  {
    id: "3",
    label: "Send Report",
    icon: <Send className="size-4" />,
    href: "#",
  },
  {
    id: "4",
    label: "Export Data",
    icon: <Download className="size-4" />,
    href: "#",
  },
];

const defaultRecentOrders: TableItem[] = [
  { id: "1", name: "Jean Dupont", email: "jean@example.com", status: "completed", amount: 99, date: "2026-02-01" },
  { id: "2", name: "Marie Martin", email: "marie@example.com", status: "active", amount: 149, date: "2026-01-30" },
  { id: "3", name: "Pierre Bernard", email: "pierre@example.com", status: "pending", amount: 299, date: "2026-01-28" },
  { id: "4", name: "Sophie Petit", email: "sophie@example.com", status: "completed", amount: 49, date: "2026-01-25" },
  { id: "5", name: "Lucas Moreau", email: "lucas@example.com", status: "cancelled", amount: 199, date: "2026-01-22" },
];

// Default mock data for preview
const defaultDashboardData: DashboardData = {
  stats: {
    totalUsers: 1247,
    totalRevenue: 45890,
    activeSubscriptions: 342,
    conversionRate: 27.4,
  },
  revenueHistory: [
    { date: "Aug", revenue: 28000, expenses: 18000 },
    { date: "Sep", revenue: 32000, expenses: 20000 },
    { date: "Oct", revenue: 38000, expenses: 22000 },
    { date: "Nov", revenue: 42000, expenses: 24000 },
    { date: "Dec", revenue: 48000, expenses: 26000 },
    { date: "Jan", revenue: 52000, expenses: 28000 },
  ],
  userGrowth: [
    { date: "Aug", users: 850 },
    { date: "Sep", users: 920 },
    { date: "Oct", users: 1010 },
    { date: "Nov", users: 1080 },
    { date: "Dec", users: 1170 },
    { date: "Jan", users: 1247 },
  ],
  recentActivity: [
    {
      id: "1",
      type: "signup",
      description: "signed up for an account",
      user: { name: "Jean Dupont", email: "jean@example.com" },
      timestamp: new Date(Date.now() - 2 * 60 * 1000).toISOString(),
    },
    {
      id: "2",
      type: "upgrade",
      description: "upgraded to Pro plan",
      user: { name: "Marie Martin", email: "marie@example.com" },
      amount: 29,
      timestamp: new Date(Date.now() - 15 * 60 * 1000).toISOString(),
    },
    {
      id: "3",
      type: "purchase",
      description: "purchased Enterprise license",
      user: { name: "Pierre Bernard", email: "pierre@example.com" },
      amount: 99,
      timestamp: new Date(Date.now() - 45 * 60 * 1000).toISOString(),
    },
    {
      id: "4",
      type: "signup",
      description: "signed up for an account",
      user: { name: "Sophie Petit", email: "sophie@example.com" },
      timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
    },
    {
      id: "5",
      type: "cancellation",
      description: "cancelled subscription",
      user: { name: "Lucas Moreau", email: "lucas@example.com" },
      timestamp: new Date(Date.now() - 3 * 60 * 60 * 1000).toISOString(),
    },
  ],
};

// =============================================================================
// COMPONENT PROPS
// =============================================================================

interface Dashboard03Props {
  data?: DashboardData | null;
  isLoading?: boolean;
  error?: string | null;
  teams?: Team[];
  user?: User;
  navigation?: NavSection[];
  quickActions?: QuickAction[];
  recentOrders?: TableItem[];
  onRefresh?: () => void;
  onSignOut?: () => void;
}

// =============================================================================
// MAIN COMPONENT
// =============================================================================

export default function Dashboard03({
  data = defaultDashboardData,
  isLoading = false,
  error = null,
  teams = defaultTeams,
  user = defaultUser,
  navigation = defaultNavigation,
  quickActions = defaultQuickActions,
  recentOrders = defaultRecentOrders,
  onRefresh,
  onSignOut,
}: Dashboard03Props) {
  const dashboardData = data ?? defaultDashboardData;

  return (
    <div className="flex h-screen bg-background w-full">
      <SidebarProvider>
        {/* Sidebar */}
        <Sidebar teams={teams} user={user} navigation={navigation} />

        {/* Main Content */}
        <main className="flex-1 overflow-y-auto bg-background">
          {/* Header */}
          <Header
            title={`Good morning, ${user.name.split(" ")[0]}`}
            subtitle="Here's what's happening with your business today."
            isLoading={isLoading}
          />

          {/* Error State */}
          {error && (
            <div className="mx-6 mt-4 p-4 bg-destructive/10 text-destructive rounded-lg">
              <p className="text-sm font-medium">Error loading dashboard data</p>
              <p className="text-xs mt-1">{error}</p>
              {onRefresh && (
                <button
                  onClick={onRefresh}
                  className="text-xs underline mt-2 hover:no-underline"
                >
                  Try again
                </button>
              )}
            </div>
          )}

          {/* Content */}
          <div className="px-6 pb-6 pt-4 space-y-4">
            {/* Stats Row */}
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
              <StatCard
                title="Total Users"
                value={dashboardData.stats.totalUsers}
                change="+12.5%"
                changeType="positive"
                description="vs last month"
                icon={<Users className="size-4" />}
                isLoading={isLoading}
              />
              <StatCard
                title="Revenue"
                value={`$${dashboardData.stats.totalRevenue.toLocaleString()}`}
                change="+8.2%"
                changeType="positive"
                description="vs last month"
                icon={<DollarSign className="size-4" />}
                isLoading={isLoading}
              />
              <StatCard
                title="Active Subscriptions"
                value={dashboardData.stats.activeSubscriptions}
                change="+3.1%"
                changeType="positive"
                description="vs last month"
                icon={<TrendingUp className="size-4" />}
                isLoading={isLoading}
              />
              <StatCard
                title="Conversion Rate"
                value={`${dashboardData.stats.conversionRate}%`}
                change="-0.5%"
                changeType="negative"
                description="vs last month"
                icon={<Activity className="size-4" />}
                isLoading={isLoading}
              />
            </div>

            {/* Charts Row */}
            <div className="grid gap-4 lg:grid-cols-2">
              <RevenueChart data={dashboardData.revenueHistory} isLoading={isLoading} />
              <UserGrowthChart data={dashboardData.userGrowth} isLoading={isLoading} />
            </div>

            {/* Bottom Row */}
            <div className="grid gap-4 lg:grid-cols-3">
              {/* Recent Activity */}
              <div className="lg:col-span-1">
                <RecentActivity
                  activities={dashboardData.recentActivity}
                  isLoading={isLoading}
                />
              </div>

              {/* Quick Actions + Recent Orders */}
              <div className="lg:col-span-2 space-y-4">
                <QuickActions actions={quickActions} isLoading={isLoading} />
                <DataTableWidget
                  title="Recent Orders"
                  items={recentOrders}
                  isLoading={isLoading}
                  viewAllHref="#"
                />
              </div>
            </div>
          </div>
        </main>
      </SidebarProvider>
    </div>
  );
}

export { Dashboard03 };

// =============================================================================
// Dashboard Types
// =============================================================================

export interface DashboardStats {
  totalUsers: number;
  totalRevenue: number;
  activeSubscriptions: number;
  conversionRate: number;
}

export interface StatCardData {
  title: string;
  value: string | number;
  change: string;
  changeType: "positive" | "negative" | "neutral";
  description: string;
  icon?: React.ReactNode;
}

export interface RevenueDataPoint {
  date: string;
  revenue: number;
  expenses?: number;
}

export interface UserGrowthDataPoint {
  date: string;
  users: number;
  newUsers?: number;
}

export interface Activity {
  id: string;
  type: "signup" | "purchase" | "upgrade" | "downgrade" | "cancellation" | "other";
  description: string;
  user?: {
    name: string;
    email: string;
    avatar?: string;
  };
  amount?: number;
  timestamp: string;
}

export interface QuickAction {
  id: string;
  label: string;
  description?: string;
  icon: React.ReactNode;
  onClick?: () => void;
  href?: string;
}

export interface DashboardData {
  stats: DashboardStats;
  revenueHistory: RevenueDataPoint[];
  userGrowth: UserGrowthDataPoint[];
  recentActivity: Activity[];
  isLoading?: boolean;
  error?: string | null;
}

// Navigation types (shared with other dashboards)
export interface NavItem {
  icon: React.ReactNode;
  label: string;
  href?: string;
  active?: boolean;
  badge?: string | number;
}

export interface NavSection {
  title: string;
  items: NavItem[];
}

export interface Team {
  name: string;
  plan: string;
  logo: React.ReactNode;
}

export interface User {
  name: string;
  email: string;
  avatar?: string;
}

// Components
export { Sidebar } from "./sidebar";
export { Header } from "./header";
export { NavUser } from "./nav-user";
export { TeamSwitcher } from "./team-switcher";
export { StatCard } from "./stat-card";
export { RevenueChart, UserGrowthChart } from "./chart-card";
export { RecentActivity } from "./recent-activity";
export { QuickActions } from "./quick-actions";
export { DataTableWidget } from "./data-table-widget";
export type { TableItem } from "./data-table-widget";

// Re-export types
export type {
  DashboardStats,
  StatCardData,
  RevenueDataPoint,
  UserGrowthDataPoint,
  Activity,
  QuickAction,
  DashboardData,
  NavItem,
  NavSection,
  Team,
  User,
} from "../types/dashboard";

"use client";

import { NavUser } from "./nav-user";
import { TeamSwitcher } from "./team-switcher";
import {
  SidebarFooter,
  SidebarGroup,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarContent,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarGroupContent,
} from "@/components/ui/sidebar";
import type { Team, User, NavSection } from "../types/dashboard";

interface SidebarProps {
  teams: Team[];
  user: User;
  navigation: NavSection[];
}

export function Sidebar({ teams, user, navigation }: SidebarProps) {
  return (
    <aside className="flex w-64 flex-col border-r bg-card">
      {/* Team Switcher */}
      <div className="px-2 py-2">
        <TeamSwitcher teams={teams} />
      </div>

      {/* Navigation */}
      <SidebarContent className="[&::-webkit-scrollbar]:hidden [-ms-overflow-style:none] [scrollbar-width:none]">
        {navigation.map((section) => (
          <SidebarGroup key={section.title}>
            <SidebarGroupLabel>{section.title}</SidebarGroupLabel>
            <SidebarGroupContent>
              <SidebarMenu>
                {section.items.map((item) => (
                  <SidebarMenuItem key={item.label}>
                    <SidebarMenuButton
                      tooltip={item.label}
                      className={item.active ? "" : "text-muted-foreground"}
                      isActive={item.active}
                    >
                      {item.icon}
                      <span>{item.label}</span>
                      {item.badge !== undefined && (
                        <span className="ml-auto text-xs bg-primary text-primary-foreground px-1.5 py-0.5 rounded-full">
                          {item.badge}
                        </span>
                      )}
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                ))}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        ))}
      </SidebarContent>

      {/* User Menu - Fixed at bottom */}
      <SidebarFooter>
        <NavUser user={user} />
      </SidebarFooter>
    </aside>
  );
}

"use client";

import { Search, Bell, Settings } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Skeleton } from "@/components/ui/skeleton";

interface Notification {
  id: string;
  title: string;
  description: string;
  timestamp: string;
  read: boolean;
}

interface HeaderProps {
  title: string;
  subtitle?: string;
  notifications?: Notification[];
  notificationCount?: number;
  onSearch?: (query: string) => void;
  isLoading?: boolean;
}

export function Header({
  title,
  subtitle,
  notifications = [],
  notificationCount,
  onSearch,
  isLoading = false,
}: HeaderProps) {
  const unreadCount = notificationCount ?? notifications.filter((n) => !n.read).length;

  if (isLoading) {
    return (
      <header className="flex items-center justify-between border-b px-6 py-4">
        <div>
          <Skeleton className="h-6 w-48 mb-1" />
          <Skeleton className="h-4 w-32" />
        </div>
        <div className="flex items-center gap-3">
          <Skeleton className="h-9 w-64" />
          <Skeleton className="size-9" />
          <Skeleton className="size-9" />
        </div>
      </header>
    );
  }

  return (
    <header className="flex items-center justify-between border-b px-6 py-4">
      <div>
        <h1 className="text-xl font-semibold">{title}</h1>
        {subtitle && <p className="text-sm text-muted-foreground">{subtitle}</p>}
      </div>

      <div className="flex items-center gap-3">
        {/* Search */}
        <div className="relative w-64">
          <Search className="absolute left-3 top-1/2 size-4 -translate-y-1/2 text-muted-foreground" />
          <Input
            type="text"
            placeholder="Search..."
            className="pl-9 bg-muted/50 border-0"
            onChange={(e) => onSearch?.(e.target.value)}
          />
        </div>

        {/* Notifications */}
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" size="icon" className="relative">
              <Bell className="size-4" />
              {unreadCount > 0 && (
                <Badge className="absolute -top-1 -right-1 size-5 flex items-center justify-center p-0 text-xs">
                  {unreadCount > 9 ? "9+" : unreadCount}
                </Badge>
              )}
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="w-80">
            <DropdownMenuLabel>Notifications</DropdownMenuLabel>
            <DropdownMenuSeparator />
            {notifications.length === 0 ? (
              <div className="py-4 text-center text-sm text-muted-foreground">
                No notifications
              </div>
            ) : (
              notifications.slice(0, 5).map((notification) => (
                <DropdownMenuItem key={notification.id} className="flex-col items-start p-3">
                  <div className="flex items-start gap-2 w-full">
                    {!notification.read && (
                      <span className="size-2 rounded-full bg-primary mt-1.5 shrink-0" />
                    )}
                    <div className="flex-1">
                      <p className="text-sm font-medium">{notification.title}</p>
                      <p className="text-xs text-muted-foreground">{notification.description}</p>
                      <p className="text-xs text-muted-foreground mt-1">{notification.timestamp}</p>
                    </div>
                  </div>
                </DropdownMenuItem>
              ))
            )}
            {notifications.length > 5 && (
              <>
                <DropdownMenuSeparator />
                <DropdownMenuItem className="justify-center text-primary">
                  View all notifications
                </DropdownMenuItem>
              </>
            )}
          </DropdownMenuContent>
        </DropdownMenu>

        {/* Settings */}
        <Button variant="ghost" size="icon">
          <Settings className="size-4" />
        </Button>
      </div>
    </header>
  );
}

"use client";

import * as React from "react";
import { ChevronsUpDown, Plus } from "lucide-react";

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuShortcut,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  useSidebar,
} from "@/components/ui/sidebar";
import type { Team } from "../types/dashboard";

interface TeamSwitcherProps {
  teams: Team[];
  onTeamChange?: (team: Team) => void;
}

export function TeamSwitcher({ teams, onTeamChange }: TeamSwitcherProps) {
  const { isMobile } = useSidebar();
  const [activeTeam, setActiveTeam] = React.useState(teams[0]);

  const handleTeamChange = (team: Team) => {
    setActiveTeam(team);
    onTeamChange?.(team);
  };

  if (!activeTeam) {
    return null;
  }

  return (
    <SidebarMenu>
      <SidebarMenuItem>
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <SidebarMenuButton
              size="lg"
              className="data-[state=open]:bg-sidebar-accent data-[state=open]:text-sidebar-accent-foreground"
            >
              <div className="bg-primary text-primary-foreground flex aspect-square size-8 items-center justify-center rounded-lg">
                {activeTeam.logo}
              </div>
              <div className="grid flex-1 text-left text-sm leading-tight">
                <span className="truncate font-medium">{activeTeam.name}</span>
                <span className="truncate text-xs">{activeTeam.plan}</span>
              </div>
              <ChevronsUpDown className="ml-auto" />
            </SidebarMenuButton>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="w-(--radix-dropdown-menu-trigger-width) min-w-56 rounded-lg"
            align="start"
            side={isMobile ? "bottom" : "right"}
            sideOffset={4}
          >
            <DropdownMenuLabel className="text-muted-foreground text-xs">
              Teams
            </DropdownMenuLabel>
            {teams.map((team, index) => (
              <DropdownMenuItem
                key={team.name}
                onClick={() => handleTeamChange(team)}
                className="gap-2 p-2"
              >
                <div className="flex size-6 items-center justify-center rounded-md border">
                  <div className="size-3.5 shrink-0">{team.logo}</div>
                </div>
                {team.name}
                <DropdownMenuShortcut>âŒ˜{index + 1}</DropdownMenuShortcut>
              </DropdownMenuItem>
            ))}
            <DropdownMenuSeparator />
            <DropdownMenuItem className="gap-2 p-2">
              <div className="flex size-6 items-center justify-center rounded-md border bg-transparent">
                <Plus className="size-4" />
              </div>
              <div className="text-muted-foreground font-medium">Add team</div>
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </SidebarMenuItem>
    </SidebarMenu>
  );
}

"use client";

import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { TrendingUp, TrendingDown, Minus } from "lucide-react";
import type { StatCardData } from "../types/dashboard";

interface StatCardProps extends StatCardData {
  isLoading?: boolean;
}

export function StatCard({
  title,
  value,
  change,
  changeType,
  description,
  icon,
  isLoading = false,
}: StatCardProps) {
  if (isLoading) {
    return (
      <Card className="p-4 shadow-none">
        <div className="flex items-center justify-between mb-2">
          <Skeleton className="h-4 w-24" />
          <Skeleton className="h-5 w-16" />
        </div>
        <Skeleton className="h-8 w-20 mb-2" />
        <Skeleton className="h-3 w-32" />
      </Card>
    );
  }

  const TrendIcon =
    changeType === "positive"
      ? TrendingUp
      : changeType === "negative"
        ? TrendingDown
        : Minus;

  const trendColor =
    changeType === "positive"
      ? "text-emerald-600"
      : changeType === "negative"
        ? "text-red-600"
        : "text-muted-foreground";

  return (
    <Card className="p-4 shadow-none">
      <div className="flex items-center justify-between mb-1">
        <div className="flex items-center gap-2">
          {icon && <span className="text-muted-foreground">{icon}</span>}
          <span className="text-sm text-muted-foreground">{title}</span>
        </div>
        <Badge variant="outline" className={`text-xs flex items-center gap-0.5 ${trendColor}`}>
          {change}
          <TrendIcon className="size-3" />
        </Badge>
      </div>
      <p className="text-3xl font-bold mb-1">
        {typeof value === "number" ? value.toLocaleString() : value}
      </p>
      <p className="text-xs text-muted-foreground">{description}</p>
    </Card>
  );
}

"use client";

import { Card } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import {
  ChartConfig,
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
} from "@/components/ui/chart";
import { Area, AreaChart, Bar, BarChart, CartesianGrid, XAxis, YAxis } from "recharts";
import type { RevenueDataPoint, UserGrowthDataPoint } from "../types/dashboard";

// =============================================================================
// Revenue Chart
// =============================================================================

interface RevenueChartProps {
  data: RevenueDataPoint[];
  isLoading?: boolean;
}

const revenueChartConfig = {
  revenue: {
    label: "Revenue",
    color: "hsl(var(--foreground))",
  },
  expenses: {
    label: "Expenses",
    color: "hsl(var(--muted-foreground))",
  },
} satisfies ChartConfig;

export function RevenueChart({ data, isLoading = false }: RevenueChartProps) {
  if (isLoading) {
    return (
      <Card className="p-4 shadow-none">
        <Skeleton className="h-5 w-32 mb-1" />
        <Skeleton className="h-3 w-24 mb-4" />
        <Skeleton className="h-[200px] w-full" />
      </Card>
    );
  }

  return (
    <Card className="p-4 shadow-none">
      <div className="flex items-center justify-between mb-1">
        <div>
          <h3 className="font-semibold text-sm">Revenue Overview</h3>
          <p className="text-xs text-muted-foreground">Monthly revenue trend</p>
        </div>
        <div className="flex items-center gap-4 text-xs">
