import { ArrowUpRight } from "lucide-react";
import { cn } from "@/lib/utils";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";

interface Hero01Props {
  badge?: string;
  heading?: string;
  description?: string;
  buttons?: {
    primary?: {
      text: string;
      url: string;
    };
    secondary?: {
      text: string;
      url: string;
    };
  };
  image?: {
    src: string;
    alt: string;
  };
  avatars?: Array<{
    src?: string;
    alt?: string;
    fallback?: string;
  }>;
  className?: string;
}

const Hero01 = ({
  badge = "5000+ Developers Using",
  heading = "Blocks built with Shadcn UI",
  description = "Production-ready components built with shadcn/ui and Tailwind CSS. Copy, paste, and customize to ship faster.",
  buttons = {
    primary: {
      text: "Browse Components",
      url: "#",
    },
    secondary: {
      text: "View Docs",
      url: "#",
    },
  },
  image = {
    src: "/placeholder.svg",
    alt: "Shadcn Components",
  },
  avatars = [
    {
      src: "https://avatar.vercel.sh/18",
      alt: "Avatar 1",
      fallback: "1",
    },
    {
      src: "https://avatar.vercel.sh/8",
      alt: "Avatar 2",
      fallback: "2",
    },
    {
      src: "https://avatar.vercel.sh/12",
      alt: "Avatar 3",
      fallback: "3",
    },
  ],
  className,
}: Hero01Props) => {
  return (
    <section className={cn("py-20 lg:py-32", className)}>
      <div className="container grid gap-8 lg:grid-cols-2">
        <div className="flex flex-col justify-center items-center lg:items-start gap-4 text-center lg:text-left">
          <Badge variant="outline" className="rounded-full" asChild>
            <a href="#" className="gap-1 w-fit mx-auto lg:mx-0">
              <Badge className="rounded-full">New</Badge>
              {badge}
              <ArrowUpRight className="size-4" />
            </a>
          </Badge>
          <h1 className="text-4xl font-semibold md:text-5xl lg:text-6xl">
            {heading}
          </h1>
          <p className="text-muted-foreground md:text-lg">{description}</p>
          <div className="flex flex-col gap-4 sm:flex-row">
            {buttons.primary && (
              <Button asChild size="lg">
                <a href={buttons.primary.url}>{buttons.primary.text}</a>
              </Button>
            )}
            {buttons.secondary && (
              <Button asChild variant="outline" size="lg">
                <a href={buttons.secondary.url} className="gap-2">
                  {buttons.secondary.text}
                  <ArrowUpRight className="size-4" />
                </a>
              </Button>
            )}
          </div>
          <div className="flex items-center gap-4">
            {avatars.length > 0 && (
              <div className="flex -space-x-2">
                {avatars.map((avatar, index) => (
                  <Avatar key={avatar.src || index}>
                    {avatar.src && (
                      <AvatarImage
                        src={avatar.src}
                        alt={avatar.alt || `Avatar ${index + 1}`}
                      />
                    )}
                    <AvatarFallback>{avatar.fallback || "CN"}</AvatarFallback>
                  </Avatar>
                ))}
              </div>
            )}
            <div className="text-sm text-muted-foreground">
              <span className="text-xl font-semibold text-primary">100+</span>
              <p>Ready-to-use components</p>
            </div>
          </div>
        </div>
        <div>
          {image && (
            <img
              src={image.src}
              alt={image.alt}
              className="w-full rounded-lg object-cover"
            />
          )}
        </div>
      </div>
    </section>
  );
};

export { Hero01 };
