import { ArrowUpRight, Zap } from "lucide-react";
import { cn } from "@/lib/utils";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";

interface GridBackgroundProps {
  className?: string;
  gridSize?: number;
  lineColor?: string;
  lineWidth?: number;
  mask?: boolean;
  maskFrom?: "top" | "bottom" | "center";
}

const GridBackground = ({
  className,
  gridSize = 64,
  lineColor = "var(--border)",
  lineWidth = 1,
  mask = true,
  maskFrom = "top",
}: GridBackgroundProps) => {
  const maskGradients = {
    top: "radial-gradient(ellipse_60%_50%_at_50%_0%,#000_70%,transparent_110%)",
    bottom:
      "radial-gradient(ellipse_60%_50%_at_50%_100%,#000_70%,transparent_110%)",
    center:
      "radial-gradient(ellipse_50%_50%_at_50%_50%,#000_40%,transparent_70%)",
  };

  return (
    <div
      className={cn(
        "pointer-events-none absolute inset-0 z-0 h-full w-full",
        className
      )}
    >
      <div
        className={cn(
          "absolute inset-0 h-full w-full",
          mask && `[mask-image:${maskGradients[maskFrom]}]`
        )}
        style={{
          backgroundImage: `linear-gradient(to right, ${lineColor} ${lineWidth}px, transparent ${lineWidth}px), linear-gradient(to bottom, ${lineColor} ${lineWidth}px, transparent ${lineWidth}px)`,
          backgroundSize: `${gridSize}px ${gridSize}px`,
          ...(mask && {
            maskImage: maskGradients[maskFrom],
            WebkitMaskImage: maskGradients[maskFrom],
          }),
        }}
      />
    </div>
  );
};

interface Hero02Props {
  badge?: string;
  heading?: string | string[];
  description?: string;
  buttons?: {
    primary?: {
      text: string;
      url: string;
    };
    secondary?: {
      text: string;
      url: string;
    };
  };
  image?: {
    src: string;
    alt: string;
  };
  className?: string;
}

const Hero02 = ({
  badge = "Your All-in-One AI SaaS Platform",
  heading = ["Blocks built with", "Shadcn & Tailwind"],
  description = "Finely crafted components built with React, Tailwind and Shadcn UI. Developers can copy and paste these blocks directly into their project.",
  buttons = {
    primary: {
      text: "Get Started",
      url: "#",
    },
    secondary: {
      text: "Learn More",
      url: "#",
    },
  },
  image = {
    src: "/placeholder.svg",
    alt: "Dashboard preview",
  },
  className,
}: Hero02Props) => {
  return (
    <section
      className={cn("relative overflow-hidden py-20 lg:py-32", className)}
    >
      <GridBackground />

      <div className="container relative z-10">
        <div className="flex flex-col items-center gap-6 text-center">
          {badge && (
            <Badge variant="secondary" className="gap-2 rounded-full py-1">
              <Zap className="size-4" />
              {badge}
            </Badge>
          )}

          <h1 className="text-4xl font-semibold md:text-5xl lg:text-6xl">
            {Array.isArray(heading)
              ? heading.map((line) => (
                  <span key={line} className="block">
                    {line}
                  </span>
                ))
              : heading}
          </h1>

          <p className="max-w-2xl text-muted-foreground md:text-lg">
            {description}
          </p>

          <div className="flex gap-4">
            {buttons.primary && (
              <Button size="lg" asChild>
                <a href={buttons.primary.url}>
                  {buttons.primary.text}
                  <ArrowUpRight className="size-4" />
                </a>
              </Button>
            )}
            {buttons.secondary && (
              <Button size="lg" variant="outline" asChild>
                <a href={buttons.secondary.url}>{buttons.secondary.text}</a>
              </Button>
            )}
          </div>

          {image && (
            <div className="mt-10 w-full max-w-5xl">
              <div className="overflow-hidden rounded-lg border shadow-2xl">
                <img
                  src={image.src}
                  alt={image.alt}
                  className="w-full aspect-video"
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

export { Hero02 };
