import { ArrowUpRight, Star } from "lucide-react";
import { cn } from "@/lib/utils";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";

interface Hero03Props {
  badge?: string;
  heading?: string;
  buttonText?: string;
  buttonUrl?: string;
  ratingText?: string;
  stats?: { value: string; label: string }[];
  image?: string;
  className?: string;
}

const Hero03 = ({
  badge = "5000+ Developers Using",
  heading = "Build Faster with  shadcn ui",
  buttonText = "Browse Components",
  buttonUrl = "#",
  ratingText = "Loved by developers worldwide",
  stats = [
    { value: "100+", label: "Components" },
    { value: "5000+", label: "Developers" },
    { value: "99%", label: "Time Saved" },
  ],
  image = "/placeholder.svg",
  className,
}: Hero03Props) => {
  return (
    <section className={cn("py-20 lg:py-32", className)}>
      <div className="container">
        <div className="grid gap-8 lg:grid-cols-2 lg:gap-12">
          <div className="flex flex-col justify-center items-center lg:items-start gap-6 text-center lg:text-left">
            <Badge
              variant="outline"
              className="rounded-full w-fit mx-auto lg:mx-0"
            >
              {badge}
            </Badge>

            <h1 className="text-4xl font-semibold md:text-5xl lg:text-6xl">
              {heading}
            </h1>

            <Button size="lg" asChild className="w-fit mx-auto lg:mx-0">
              <a href={buttonUrl} className="gap-2">
                {buttonText}
                <ArrowUpRight className="size-4" />
              </a>
            </Button>

            <div className="flex items-center gap-2 mx-auto lg:mx-0">
              <div className="flex">
                {[...Array(5)].map((_, i) => (
                  <Star
                    key={`star-${i}`}
                    className="size-4 fill-primary text-primary"
                  />
                ))}
              </div>
              <Separator orientation="vertical" className="h-4" />
              <span className="text-sm text-muted-foreground">
                {ratingText}
              </span>
            </div>

            <Card className="w-full">
              <div className="flex divide-x">
                {stats.map((stat) => (
                  <div
                    key={stat.label}
                    className="flex flex-1 flex-col items-center py-6"
                  >
                    <span className="lg:text-2xl text-xl font-semibold">
                      {stat.value}
                    </span>
                    <span className="text-sm text-muted-foreground">
                      {stat.label}
                    </span>
                  </div>
                ))}
              </div>
            </Card>
          </div>

          <div className="overflow-hidden rounded-lg">
            <img src={image} alt={heading} className="w-full object-cover" />
          </div>
        </div>
      </div>
    </section>
  );
};

export { Hero03 };
