import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";

interface LineBackgroundProps {
  className?: string;
  lineCount?: number;
  lineWidth?: number;
  lineColor?: string;
  gap?: number;
}

const LineBackground = ({
  className,
  lineCount = 12,
  lineWidth = 1,
  lineColor = "var(--border)",
  gap,
}: LineBackgroundProps) => {
  return (
    <div
      className={cn(
        "pointer-events-none absolute inset-0 z-0 overflow-hidden",
        className
      )}
    >
      <div
        className="flex size-full items-stretch justify-between"
        style={{ padding: gap ? `0 ${gap}px` : undefined }}
      >
        {Array.from({ length: lineCount }).map((_, i) => (
          <div
            key={i}
            style={{
              width: `${lineWidth}px`,
              backgroundColor: lineColor,
            }}
          />
        ))}
      </div>
    </div>
  );
};

interface Hero04Props {
  badge?: {
    text: string;
    avatars?: string[];
  };
  heading?: string;
  description?: string;
  buttons?: {
    primary?: {
      text: string;
      url: string;
    };
    secondary?: {
      text: string;
      url: string;
    };
  };
  className?: string;
}

const Hero04 = ({
  badge = {
    text: "5000+ Developers Using",
    avatars: [
      "https://avatar.vercel.sh/18",
      "https://avatar.vercel.sh/8",
      "https://avatar.vercel.sh/12",
    ],
  },
  heading = "Build Faster with\nReady-to-Use Components",
  description = "Copy, paste, and customize. Production-ready components built with shadcn/ui and Tailwind CSS.",
  buttons = {
    primary: {
      text: "Browse Components",
      url: "#",
    },
    secondary: {
      text: "View Documentation",
      url: "#",
    },
  },
  className,
}: Hero04Props) => {
  return (
    <section
      className={cn(
        "relative flex min-h-screen items-center overflow-hidden py-20 lg:py-32",
        className
      )}
    >
      <LineBackground
        className="z-0"
        lineCount={14}
        lineColor="var(--border)"
        gap={0}
      />

      <div className="pointer-events-none absolute inset-0 z-[1] bg-[radial-gradient(ellipse_100%_60%_at_50%_50%,var(--background)_0%,var(--background)_40%,transparent_70%)] md:bg-[radial-gradient(ellipse_70%_50%_at_50%_50%,var(--background)_0%,var(--background)_40%,transparent_70%)]" />
      <div className="container relative z-10 flex flex-col items-center gap-6 text-center">
        {badge && (
          <Badge variant="outline" className="gap-2 rounded-full py-1">
            <div className="flex -space-x-2">
              {badge.avatars?.map((avatar, i) => (
                <Avatar key={`avatar-${i}`} className="size-5">
                  <AvatarImage src={avatar} />
                  <AvatarFallback>U</AvatarFallback>
                </Avatar>
              ))}
            </div>
            <span className="font-medium italic">{badge.text}</span>
          </Badge>
        )}

        <h1 className="max-w-5xl text-4xl font-bold md:text-5xl lg:text-6xl">
          {heading.split("\n").map((line, i) => (
            <span key={`line-${i}`}>
              {line}
              {i < heading.split("\n").length - 1 && <br />}
            </span>
          ))}
        </h1>

        <p className="max-w-xl text-muted-foreground md:text-lg">
          {description}
        </p>

        <div className="flex flex-col gap-4 md:flex-row">
          {buttons?.primary && (
            <Button size="lg" asChild>
              <a href={buttons.primary.url}>{buttons.primary.text}</a>
            </Button>
          )}
          {buttons?.secondary && (
            <Button variant="outline" size="lg" asChild>
              <a href={buttons.secondary.url}>{buttons.secondary.text}</a>
            </Button>
          )}
        </div>
      </div>
    </section>
  );
};

export { Hero04 };
