import { ArrowUpRight, Circle } from "lucide-react";
import { cn } from "@/lib/utils";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";

interface Hero05Props {
  badge?: string;
  heading?: string | string[];
  description?: string;
  buttons?: {
    primary?: {
      text: string;
      url: string;
    };
    secondary?: {
      text: string;
      url: string;
    };
  };
  className?: string;
}

const Hero05 = ({
  badge = "Beta version",
  heading = ["Discover the", "Future of Web Development"],
  description = "We are building the future of web development with a focus on performance, accessibility, and user experience.",
  buttons = {
    primary: {
      text: "Get Started",
      url: "#",
    },
    secondary: {
      text: "Learn More",
      url: "#",
    },
  },
  className,
}: Hero05Props) => {
  return (
    <section
      className={cn(
        "relative overflow-hidden flex min-h-screen items-center py-20 lg:py-32",
        className
      )}
    >
      <div className="container relative z-10">
        <div className="flex flex-col items-center gap-6 text-center">
          {badge && (
            <Badge variant="outline" className="gap-2 rounded-full">
              <Circle className="size-4 fill-primary" />
              {badge}
            </Badge>
          )}

          <h1 className="max-w-5xl text-4xl font-semibold md:text-5xl lg:text-6xl">
            {Array.isArray(heading)
              ? heading.map((line, index) => (
                  <span key={index} className="block">
                    {line}
                  </span>
                ))
              : heading}
          </h1>

          <p className="max-w-2xl text-muted-foreground md:text-lg">
            {description}
          </p>

          <div className="flex gap-4">
            {buttons.primary && (
              <Button size="lg" asChild>
                <a href={buttons.primary.url}>
                  {buttons.primary.text}
                  <ArrowUpRight className="size-4" />
                </a>
              </Button>
            )}
            {buttons.secondary && (
              <Button size="lg" variant="outline" asChild>
                <a href={buttons.secondary.url}>{buttons.secondary.text}</a>
              </Button>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};

export { Hero05 };
