const IntegrationIconBubble = ({
  icon,
  className,
  size = "md",
}: IntegrationIcon & { size?: "sm" | "md" | "lg" }) => {
  const sizeClasses = {
    sm: "size-10",
    md: "size-12",
    lg: "size-14",
  };

  return (
    <div
      className={cn(
        "flex items-center justify-center rounded-full border bg-background",
        sizeClasses[size],
        className,
      )}
    >
      {icon}
    </div>
  );
};

const Integration01 = ({
  tagline = "Integrations",
  heading = "Integrates with your workflow",
  description = "Collect your web snippets, Kindle highlights and important links, all in one place. Then quickly find them again from any device.",
  integrations = [
    { icon: <DefaultIcons.Settings className="text-muted-foreground" /> },
    { icon: <DefaultIcons.Rss className="text-primary" /> },
    { icon: <DefaultIcons.Notion /> },
    { icon: <DefaultIcons.Dropbox /> },
    { icon: <DefaultIcons.Asterisk className="text-primary" /> },
    { icon: <DefaultIcons.Slack /> },
    { icon: <DefaultIcons.Chevrons className="text-primary" /> },
    { icon: <DefaultIcons.Grid className="text-muted-foreground" /> },
    { icon: <DefaultIcons.Menu className="text-muted-foreground" /> },
  ],
  testimonial = {
    quote:
      "Revilo has amazing integrations with Brex, Mercury, and Stripe. Onboarding was super fast and smooth. Now I can finally see everything in one place.",
    author: "Nathan Bamford",
    role: "Revenue Operations Lead at ScaleX",
    avatar: "https://www.shadcnship.com/images/image-preview.webp",
  },
  className,
}: Integration01Props) => {
  // Calculate positions on a flat elliptical arc (quadratic bezier curve)
  // Curve goes from (0, 100) through (50, 30) to (100, 100)
  const getPositionOnCurve = (index: number, total: number) => {
    // t goes from 0 to 1 across all icons
    const t = index / (total - 1);

    // Quadratic bezier: P = (1-t)^2 * P0 + 2(1-t)t * P1 + t^2 * P2
    // P0 = (0, 100), P1 = (50, 30), P2 = (100, 100)
    const p0 = { x: 0, y: 100 };
    const p1 = { x: 50, y: 30 }; // Control point - lower = flatter curve
    const p2 = { x: 100, y: 100 };

    const x =
      Math.pow(1 - t, 2) * p0.x + 2 * (1 - t) * t * p1.x + Math.pow(t, 2) * p2.x;
    const y =
      Math.pow(1 - t, 2) * p0.y + 2 * (1 - t) * t * p1.y + Math.pow(t, 2) * p2.y;

    return { x, y };
  };

  const iconCount = integrations.length;

  return (
    <section className={cn("container mx-auto px-6 py-12 md:py-24", className)}>
      {/* Header */}
      <div className="mx-auto max-w-2xl text-center">
        <p className="text-sm font-medium">{tagline}</p>
        <h2 className="mt-2 text-4xl font-semibold tracking-tight md:text-5xl">
          {heading}
        </h2>
        <p className="mt-4 text-muted-foreground">{description}</p>
      </div>

      {/* Integration visualization */}
      <div className="relative mx-auto mt-12 max-w-4xl">
        {/* Container for the arc */}
        <div className="relative aspect-[3/1]">
          {/* SVG curve - quadratic bezier from left to right */}
          <svg
            className="absolute inset-0 size-full overflow-visible"
            viewBox="0 0 100 100"
            preserveAspectRatio="none"
            fill="none"
          >
            <path
              d="M 0 100 Q 50 30 100 100"
              stroke="currentColor"
              strokeWidth="1"
              fill="none"
              className="text-border"
              vectorEffect="non-scaling-stroke"
            />
          </svg>

          {/* Integration icons positioned along the curve */}
          {integrations.map((integration, index) => {
            const pos = getPositionOnCurve(index, iconCount);
            const isCenter = index === Math.floor(iconCount / 2);

            return (
              <div
                key={index}
                className="absolute -translate-x-1/2 -translate-y-1/2"
                style={{
                  left: `${pos.x}%`,
                  top: `${pos.y}%`,
                }}
              >
                <IntegrationIconBubble
                  icon={integration.icon}
                  size={isCenter ? "lg" : "md"}
                  className={isCenter ? "border-primary" : ""}
                />
              </div>
            );
          })}
        </div>

        {/* Testimonial */}
        <div className="mx-auto mt-8 max-w-lg text-center">
          <p className="text-sm md:text-base">
            &ldquo;{testimonial.quote}&rdquo;
          </p>
          <div className="mt-4 flex items-center justify-center gap-3">
            {testimonial.avatar && (
              <img
                src={testimonial.avatar}
                alt={testimonial.author}
                className="size-10 rounded-full object-cover"
              />
            )}
            <div className="text-left">
              <p className="text-sm font-medium">{testimonial.author}</p>
              <p className="text-xs text-muted-foreground">
                {testimonial.role}
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export { Integration01 };
