"use client";

import { cn } from "@/lib/utils";
import { Card, CardContent } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import {
  InvoiceHeader,
  InvoiceAddresses,
  InvoiceLineItems,
  InvoiceTotals,
  InvoiceFooter,
  InvoiceActions,
} from "./components";
import type { Invoice, InvoiceProps } from "./types/invoice";

// =============================================================================
// Demo Invoice Data
// =============================================================================

const demoInvoice: Invoice = {
  id: "inv-001",
  number: "INV-2024-001",
  status: "pending",
  issueDate: "2024-01-15",
  dueDate: "2024-02-15",
  currency: "USD",
  company: {
    name: "Acme Inc.",
    logo: undefined,
    website: "www.acme.com",
    taxId: "US123456789",
    address: {
      name: "Acme Inc.",
      address: "123 Business Ave, Suite 100",
      city: "San Francisco",
      state: "CA",
      postalCode: "94102",
      country: "United States",
      email: "billing@acme.com",
      phone: "+1 (555) 123-4567",
    },
  },
  billTo: {
    name: "John Doe",
    company: "Tech Startup LLC",
    address: "456 Innovation Way",
    city: "Austin",
    state: "TX",
    postalCode: "78701",
    country: "United States",
    email: "john@techstartup.com",
  },
  lineItems: [
    {
      id: "1",
      description: "Website Design & Development",
      quantity: 1,
      unitPrice: 5000,
      total: 5000,
    },
    {
      id: "2",
      description: "Logo Design Package",
      quantity: 1,
      unitPrice: 1500,
      total: 1500,
    },
    {
      id: "3",
      description: "Monthly Hosting (12 months)",
      quantity: 12,
      unitPrice: 29,
      total: 348,
    },
    {
      id: "4",
      description: "SEO Optimization",
      quantity: 1,
      unitPrice: 800,
      total: 800,
    },
  ],
  subtotal: 7648,
  taxRate: 8.25,
  taxAmount: 630.96,
  discount: 500,
  total: 7778.96,
  paymentTerms: "Payment is due within 30 days of invoice date. Please include the invoice number with your payment.",
  notes: "Thank you for choosing Acme Inc. We appreciate your business and look forward to working with you again!",
};

// =============================================================================
// Print Styles (injected into document)
// =============================================================================

const printStyles = `
  @media print {
    body * {
      visibility: hidden;
    }
    .invoice-container, .invoice-container * {
      visibility: visible;
    }
    .invoice-container {
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      padding: 20px;
    }
    .print\\:hidden {
      display: none !important;
    }
  }
`;

// =============================================================================
// Main Component
// =============================================================================

export default function Invoice01({
  invoice = demoInvoice,
  onPrint,
  onDownload,
  className,
}: InvoiceProps) {
  return (
    <>
      {/* Print styles */}
      <style dangerouslySetInnerHTML={{ __html: printStyles }} />

      <div className={cn("w-full max-w-4xl mx-auto", className)}>
        {/* Actions Bar */}
        <div className="flex justify-end mb-4">
          <InvoiceActions onPrint={onPrint} onDownload={onDownload} />
        </div>

        {/* Invoice Card */}
        <Card className="invoice-container">
          <CardContent className="p-8 space-y-8">
            {/* Header */}
            <InvoiceHeader invoice={invoice} />

            <Separator />

            {/* Addresses */}
            <InvoiceAddresses company={invoice.company} billTo={invoice.billTo} />

            {/* Line Items */}
            <InvoiceLineItems items={invoice.lineItems} currency={invoice.currency} />

            {/* Totals */}
            <InvoiceTotals invoice={invoice} />

            {/* Footer */}
            <InvoiceFooter invoice={invoice} />
          </CardContent>
        </Card>
      </div>
    </>
  );
}

// =============================================================================
// Named Exports
// =============================================================================

export { Invoice01 };

// Re-export types
export type {
  Invoice,
  InvoiceProps,
  InvoiceStatus,
  InvoiceAddress,
  InvoiceLineItem,
  InvoiceCompany,
} from "./types/invoice";

// =============================================================================
// Invoice Types
// =============================================================================

export type InvoiceStatus = "draft" | "pending" | "paid" | "overdue" | "cancelled";

export interface InvoiceAddress {
  name: string;
  company?: string;
  address: string;
  city: string;
  state?: string;
  postalCode: string;
  country: string;
  email?: string;
  phone?: string;
}

export interface InvoiceLineItem {
  id: string;
  description: string;
  quantity: number;
  unitPrice: number;
  total: number;
}

export interface InvoiceCompany {
  name: string;
  logo?: string;
  address: InvoiceAddress;
  taxId?: string;
  website?: string;
}

export interface Invoice {
  id: string;
  number: string;
  status: InvoiceStatus;
  issueDate: string;
  dueDate: string;
  company: InvoiceCompany;
  billTo: InvoiceAddress;
  lineItems: InvoiceLineItem[];
  subtotal: number;
  taxRate?: number;
  taxAmount?: number;
  discount?: number;
  total: number;
  currency: string;
  notes?: string;
  paymentTerms?: string;
}

export interface InvoiceProps {
  invoice?: Invoice;
  onPrint?: () => void;
  onDownload?: () => void;
  className?: string;
}

// Components
export { InvoiceHeader } from "./invoice-header";
export { InvoiceAddresses } from "./invoice-addresses";
export { InvoiceLineItems } from "./invoice-line-items";
export { InvoiceTotals } from "./invoice-totals";
export { InvoiceFooter } from "./invoice-footer";
export { InvoiceActions } from "./invoice-actions";

// Re-export types
export type {
  InvoiceStatus,
  InvoiceAddress,
  InvoiceLineItem,
  InvoiceCompany,
  Invoice,
  InvoiceProps,
} from "../types/invoice";

"use client";

import { cn } from "@/lib/utils";
import { Badge } from "@/components/ui/badge";
import type { Invoice, InvoiceStatus } from "../types/invoice";

interface InvoiceHeaderProps {
  invoice: Invoice;
  className?: string;
}

const statusConfig: Record<InvoiceStatus, { label: string; variant: "default" | "secondary" | "destructive" | "outline" }> = {
  draft: { label: "Draft", variant: "secondary" },
  pending: { label: "Pending", variant: "outline" },
  paid: { label: "Paid", variant: "default" },
  overdue: { label: "Overdue", variant: "destructive" },
  cancelled: { label: "Cancelled", variant: "secondary" },
};

export function InvoiceHeader({ invoice, className }: InvoiceHeaderProps) {
  const status = statusConfig[invoice.status];

  return (
    <div className={cn("flex flex-col sm:flex-row justify-between gap-6", className)}>
      {/* Company Logo & Info */}
      <div className="space-y-1">
        {invoice.company.logo ? (
          <img
            src={invoice.company.logo}
            alt={invoice.company.name}
            className="h-10 w-auto object-contain"
          />
        ) : (
          <h2 className="text-2xl font-bold">{invoice.company.name}</h2>
        )}
        {invoice.company.website && (
          <p className="text-sm text-muted-foreground">{invoice.company.website}</p>
        )}
        {invoice.company.taxId && (
          <p className="text-sm text-muted-foreground">Tax ID: {invoice.company.taxId}</p>
        )}
      </div>

      {/* Invoice Info */}
      <div className="text-left sm:text-right space-y-1">
        <div className="flex items-center gap-3 sm:justify-end">
          <h1 className="text-2xl font-bold">Invoice</h1>
          <Badge variant={status.variant}>{status.label}</Badge>
        </div>
        <p className="text-lg font-mono text-muted-foreground">#{invoice.number}</p>
        <div className="text-sm text-muted-foreground space-y-0.5">
          <p>Issue Date: {formatDate(invoice.issueDate)}</p>
          <p>Due Date: {formatDate(invoice.dueDate)}</p>
        </div>
      </div>
    </div>
  );
}

function formatDate(dateString: string): string {
  return new Date(dateString).toLocaleDateString("en-US", {
    year: "numeric",
    month: "short",
    day: "numeric",
  });
}

"use client";

import { cn } from "@/lib/utils";
import type { InvoiceAddress, InvoiceCompany } from "../types/invoice";

interface InvoiceAddressesProps {
  company: InvoiceCompany;
  billTo: InvoiceAddress;
  className?: string;
}

function AddressBlock({ title, address }: { title: string; address: InvoiceAddress }) {
  return (
    <div className="space-y-2">
      <p className="text-xs font-medium uppercase tracking-wider text-muted-foreground">
        {title}
      </p>
      <div className="text-sm space-y-0.5">
        <p className="font-medium">{address.name}</p>
        {address.company && <p>{address.company}</p>}
        <p>{address.address}</p>
        <p>
          {address.city}
          {address.state && `, ${address.state}`} {address.postalCode}
        </p>
        <p>{address.country}</p>
        {address.email && (
          <p className="text-muted-foreground">{address.email}</p>
        )}
        {address.phone && (
          <p className="text-muted-foreground">{address.phone}</p>
        )}
      </div>
    </div>
  );
}

export function InvoiceAddresses({ company, billTo, className }: InvoiceAddressesProps) {
  return (
    <div className={cn("grid grid-cols-1 sm:grid-cols-2 gap-8", className)}>
      <AddressBlock title="From" address={company.address} />
      <AddressBlock title="Bill To" address={billTo} />
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import type { InvoiceLineItem } from "../types/invoice";

interface InvoiceLineItemsProps {
  items: InvoiceLineItem[];
  currency: string;
  className?: string;
}

export function InvoiceLineItems({ items, currency, className }: InvoiceLineItemsProps) {
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: currency,
    }).format(amount);
  };

  return (
    <div className={cn("border rounded-lg overflow-hidden", className)}>
      <Table>
        <TableHeader>
          <TableRow className="bg-muted/50">
            <TableHead className="w-[50%]">Description</TableHead>
            <TableHead className="text-right">Qty</TableHead>
            <TableHead className="text-right">Unit Price</TableHead>
            <TableHead className="text-right">Amount</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {items.map((item) => (
            <TableRow key={item.id}>
              <TableCell className="font-medium">{item.description}</TableCell>
              <TableCell className="text-right">{item.quantity}</TableCell>
              <TableCell className="text-right">{formatCurrency(item.unitPrice)}</TableCell>
              <TableCell className="text-right font-medium">
                {formatCurrency(item.total)}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { Separator } from "@/components/ui/separator";
import type { Invoice } from "../types/invoice";

interface InvoiceTotalsProps {
  invoice: Invoice;
  className?: string;
}

export function InvoiceTotals({ invoice, className }: InvoiceTotalsProps) {
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: invoice.currency,
    }).format(amount);
  };

  return (
    <div className={cn("flex justify-end", className)}>
      <div className="w-full max-w-xs space-y-2">
        <div className="flex justify-between text-sm">
          <span className="text-muted-foreground">Subtotal</span>
          <span>{formatCurrency(invoice.subtotal)}</span>
        </div>

        {invoice.discount !== undefined && invoice.discount > 0 && (
          <div className="flex justify-between text-sm">
            <span className="text-muted-foreground">Discount</span>
            <span className="text-green-600">-{formatCurrency(invoice.discount)}</span>
          </div>
        )}

        {invoice.taxRate !== undefined && invoice.taxAmount !== undefined && (
          <div className="flex justify-between text-sm">
            <span className="text-muted-foreground">Tax ({invoice.taxRate}%)</span>
            <span>{formatCurrency(invoice.taxAmount)}</span>
          </div>
        )}

        <Separator className="my-2" />

        <div className="flex justify-between text-lg font-semibold">
          <span>Total</span>
          <span>{formatCurrency(invoice.total)}</span>
        </div>

        <div className="flex justify-between text-xs text-muted-foreground">
          <span>Amount Due ({invoice.currency})</span>
          <span className="font-medium text-foreground text-base">
            {formatCurrency(invoice.status === "paid" ? 0 : invoice.total)}
          </span>
        </div>
      </div>
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { Separator } from "@/components/ui/separator";
import type { Invoice } from "../types/invoice";

interface InvoiceFooterProps {
  invoice: Invoice;
  className?: string;
}

export function InvoiceFooter({ invoice, className }: InvoiceFooterProps) {
  if (!invoice.notes && !invoice.paymentTerms) return null;

  return (
    <div className={cn("space-y-4", className)}>
      <Separator />

      {invoice.paymentTerms && (
        <div className="space-y-1">
          <h4 className="text-sm font-medium">Payment Terms</h4>
          <p className="text-sm text-muted-foreground">{invoice.paymentTerms}</p>
        </div>
      )}

      {invoice.notes && (
        <div className="space-y-1">
          <h4 className="text-sm font-medium">Notes</h4>
          <p className="text-sm text-muted-foreground">{invoice.notes}</p>
        </div>
      )}

      <div className="text-center text-xs text-muted-foreground pt-4">
        <p>Thank you for your business!</p>
      </div>
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Download, Printer } from "lucide-react";

interface InvoiceActionsProps {
  onPrint?: () => void;
  onDownload?: () => void;
  className?: string;
}

export function InvoiceActions({ onPrint, onDownload, className }: InvoiceActionsProps) {
  const handlePrint = () => {
    if (onPrint) {
      onPrint();
    } else {
      window.print();
    }
  };

  return (
    <div className={cn("flex gap-2 print:hidden", className)}>
      <Button variant="outline" size="sm" onClick={handlePrint}>
        <Printer className="size-4 mr-2" />
        Print
      </Button>
      {onDownload && (
        <Button variant="outline" size="sm" onClick={onDownload}>
          <Download className="size-4 mr-2" />
          Download PDF
        </Button>
      )}
    </div>
  );
}
