"use client";

import { useState } from "react";
import { cn } from "@/lib/utils";
import { Card, CardContent } from "@/components/ui/card";
import {
  StepIndicator,
  StepWelcome,
  StepProfile,
  StepPreferences,
  StepWorkspace,
  StepComplete,
} from "./components";
import type {
  OnboardingStep,
  OnboardingData,
  OnboardingProfile,
  OnboardingPreferences,
  OnboardingWorkspace,
  OnboardingProps,
} from "./types/onboarding";

// =============================================================================
// Step Order
// =============================================================================

const stepOrder: OnboardingStep[] = ["welcome", "profile", "preferences", "workspace", "complete"];

// =============================================================================
// Default Data
// =============================================================================

const defaultData: OnboardingData = {
  profile: {
    name: "",
    avatar: undefined,
    role: undefined,
  },
  preferences: {
    theme: "system",
    emailNotifications: true,
    weeklyDigest: true,
  },
  workspace: {
    workspaceName: "",
    teamSize: "just-me",
    industry: undefined,
  },
};

// =============================================================================
// Main Component
// =============================================================================

export default function Onboarding01({
  defaultStep = "welcome",
  onComplete,
  onStepChange,
  onSkip,
  showConfetti = true,
  className,
}: OnboardingProps) {
  const [currentStep, setCurrentStep] = useState<OnboardingStep>(defaultStep);
  const [data, setData] = useState<OnboardingData>(defaultData);
  const [isLoading, setIsLoading] = useState(false);

  const goToStep = (step: OnboardingStep) => {
    setCurrentStep(step);
    onStepChange?.(step);
  };

  const goToNextStep = () => {
    const currentIndex = stepOrder.indexOf(currentStep);
    if (currentIndex < stepOrder.length - 1) {
      goToStep(stepOrder[currentIndex + 1]);
    }
  };

  const goToPreviousStep = () => {
    const currentIndex = stepOrder.indexOf(currentStep);
    if (currentIndex > 0) {
      goToStep(stepOrder[currentIndex - 1]);
    }
  };

  const handleProfileChange = (profile: OnboardingProfile) => {
    setData((prev) => ({ ...prev, profile }));
  };

  const handlePreferencesChange = (preferences: OnboardingPreferences) => {
    setData((prev) => ({ ...prev, preferences }));
  };

  const handleWorkspaceChange = (workspace: OnboardingWorkspace) => {
    setData((prev) => ({ ...prev, workspace }));
  };

  const handleWorkspaceSubmit = async () => {
    setIsLoading(true);
    try {
      await onComplete?.(data);
      goToNextStep();
    } catch (error) {
      console.error("Failed to complete onboarding:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFinish = () => {
    console.log("Onboarding complete:", data);
    // Navigate to dashboard or close modal
  };

  const handleSkip = () => {
    onSkip?.();
    handleFinish();
  };

  const renderStep = () => {
    switch (currentStep) {
      case "welcome":
        return <StepWelcome onNext={goToNextStep} />;
      case "profile":
        return (
          <StepProfile
            data={data.profile}
            onChange={handleProfileChange}
            onNext={goToNextStep}
            onBack={goToPreviousStep}
            onSkip={handleSkip}
          />
        );
      case "preferences":
        return (
          <StepPreferences
            data={data.preferences}
            onChange={handlePreferencesChange}
            onNext={goToNextStep}
            onBack={goToPreviousStep}
            onSkip={handleSkip}
          />
        );
      case "workspace":
        return (
          <StepWorkspace
            data={data.workspace}
            onChange={handleWorkspaceChange}
            onNext={handleWorkspaceSubmit}
            onBack={goToPreviousStep}
            onSkip={handleSkip}
            isLoading={isLoading}
          />
        );
      case "complete":
        return (
          <StepComplete
            workspaceName={data.workspace.workspaceName || "My Workspace"}
            showConfetti={showConfetti}
            onFinish={handleFinish}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className={cn("flex items-center justify-center min-h-[600px] p-4", className)}>
      <Card className="w-full max-w-lg">
        <CardContent className="p-6 sm:p-8">
          {/* Step Indicator */}
          <StepIndicator currentStep={currentStep} className="mb-8" />

          {/* Current Step Content */}
          {renderStep()}
        </CardContent>
      </Card>
    </div>
  );
}

// =============================================================================
// Named Exports
// =============================================================================

export { Onboarding01 };

// Re-export types
export type {
  OnboardingStep,
  OnboardingProfile,
  OnboardingPreferences,
  OnboardingWorkspace,
  OnboardingData,
  OnboardingState,
  OnboardingProps,
} from "./types/onboarding";

// =============================================================================
// Onboarding Types
// =============================================================================

export type OnboardingStep = "welcome" | "profile" | "preferences" | "workspace" | "complete";

export interface OnboardingProfile {
  name: string;
  avatar?: string;
  role?: string;
}

export interface OnboardingPreferences {
  theme: "light" | "dark" | "system";
  emailNotifications: boolean;
  weeklyDigest: boolean;
}

export interface OnboardingWorkspace {
  workspaceName: string;
  teamSize: "just-me" | "small" | "medium" | "large";
  industry?: string;
}

export interface OnboardingData {
  profile: OnboardingProfile;
  preferences: OnboardingPreferences;
  workspace: OnboardingWorkspace;
}

export interface OnboardingState {
  currentStep: OnboardingStep;
  data: Partial<OnboardingData>;
  isLoading: boolean;
  error: string | null;
}

export interface OnboardingProps {
  defaultStep?: OnboardingStep;
  onComplete?: (data: OnboardingData) => Promise<void>;
  onStepChange?: (step: OnboardingStep) => void;
  onSkip?: () => void;
  showConfetti?: boolean;
  className?: string;
}

export interface StepProps {
  onNext: () => void;
  onBack?: () => void;
  onSkip?: () => void;
  isLoading?: boolean;
}

// Components
export { StepIndicator } from "./step-indicator";
export { StepWelcome } from "./step-welcome";
export { StepProfile } from "./step-profile";
export { StepPreferences } from "./step-preferences";
export { StepWorkspace } from "./step-workspace";
export { StepComplete } from "./step-complete";
export { NavigationButtons } from "./navigation-buttons";

// Re-export types
export type {
  OnboardingStep,
  OnboardingProfile,
  OnboardingPreferences,
  OnboardingWorkspace,
  OnboardingData,
  OnboardingState,
  OnboardingProps,
  StepProps,
} from "../types/onboarding";

"use client";

import { cn } from "@/lib/utils";
import { Check } from "lucide-react";
import type { OnboardingStep } from "../types/onboarding";

interface StepIndicatorProps {
  currentStep: OnboardingStep;
  className?: string;
}

const steps: { id: OnboardingStep; label: string }[] = [
  { id: "welcome", label: "Welcome" },
  { id: "profile", label: "Profile" },
  { id: "preferences", label: "Preferences" },
  { id: "workspace", label: "Workspace" },
  { id: "complete", label: "Complete" },
];

const stepOrder: OnboardingStep[] = ["welcome", "profile", "preferences", "workspace", "complete"];

export function StepIndicator({ currentStep, className }: StepIndicatorProps) {
  const currentIndex = stepOrder.indexOf(currentStep);

  return (
    <div className={cn("w-full", className)}>
      {/* Desktop view */}
      <div className="hidden sm:flex items-center justify-center">
        {steps.map((step, index) => {
          const isCompleted = index < currentIndex;
          const isCurrent = index === currentIndex;
          const isLast = index === steps.length - 1;

          return (
            <div key={step.id} className="flex items-center">
              <div className="flex flex-col items-center">
                <div
                  className={cn(
                    "size-8 rounded-full flex items-center justify-center text-sm font-medium transition-colors",
                    isCompleted && "bg-primary text-primary-foreground",
                    isCurrent && "bg-primary text-primary-foreground",
                    !isCompleted && !isCurrent && "bg-muted text-muted-foreground"
                  )}
                >
                  {isCompleted ? (
                    <Check className="size-4" />
                  ) : (
                    <span>{index + 1}</span>
                  )}
                </div>
                <span
                  className={cn(
                    "mt-2 text-xs font-medium",
                    isCurrent && "text-primary",
                    !isCurrent && "text-muted-foreground"
                  )}
                >
                  {step.label}
                </span>
              </div>
              {!isLast && (
                <div
                  className={cn(
                    "w-12 h-0.5 mx-2 mb-6",
                    index < currentIndex ? "bg-primary" : "bg-muted"
                  )}
                />
              )}
            </div>
          );
        })}
      </div>

      {/* Mobile view - progress bar */}
      <div className="sm:hidden space-y-2">
        <div className="flex justify-between text-sm">
          <span className="font-medium">
            Step {currentIndex + 1} of {steps.length}
          </span>
          <span className="text-muted-foreground">
            {steps[currentIndex].label}
          </span>
        </div>
        <div className="h-2 bg-muted rounded-full overflow-hidden">
          <div
            className="h-full bg-primary transition-all duration-300"
            style={{ width: `${((currentIndex + 1) / steps.length) * 100}%` }}
          />
        </div>
      </div>
    </div>
  );
}

