"use client";

import { useState } from "react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Camera } from "lucide-react";
import { NavigationButtons } from "./navigation-buttons";
import type { OnboardingProfile } from "../types/onboarding";

interface StepProfileProps {
  data: OnboardingProfile;
  onChange: (data: OnboardingProfile) => void;
  onNext: () => void;
  onBack: () => void;
  onSkip?: () => void;
  className?: string;
}

const roles = [
  { value: "developer", label: "Developer" },
  { value: "designer", label: "Designer" },
  { value: "product", label: "Product Manager" },
  { value: "marketing", label: "Marketing" },
  { value: "sales", label: "Sales" },
  { value: "support", label: "Support" },
  { value: "other", label: "Other" },
];

export function StepProfile({
  data,
  onChange,
  onNext,
  onBack,
  onSkip,
  className,
}: StepProfileProps) {
  const [errors, setErrors] = useState<Record<string, string>>({});

  const handleChange = (field: keyof OnboardingProfile, value: string) => {
    onChange({ ...data, [field]: value });
    if (errors[field]) {
      setErrors((prev) => ({ ...prev, [field]: "" }));
    }
  };

  const validate = (): boolean => {
    const newErrors: Record<string, string> = {};
    if (!data.name?.trim()) {
      newErrors.name = "Name is required";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validate()) {
      onNext();
    }
  };

  return (
    <div className={cn("space-y-6", className)}>
      <div className="space-y-2 text-center">
        <h2 className="text-2xl font-bold tracking-tight">Set up your profile</h2>
        <p className="text-muted-foreground">
          Tell us a bit about yourself so we can personalize your experience.
        </p>
      </div>

      <div className="space-y-4 max-w-sm mx-auto">
        {/* Avatar */}
        <div className="flex justify-center">
          <div className="relative">
            <Avatar className="size-24">
              <AvatarImage src={data.avatar} />
              <AvatarFallback className="text-2xl">
                {data.name?.slice(0, 2).toUpperCase() || "?"}
              </AvatarFallback>
            </Avatar>
            <Button
              type="button"
              variant="secondary"
              size="icon"
              className="absolute -bottom-1 -right-1 size-8 rounded-full"
            >
              <Camera className="size-4" />
            </Button>
          </div>
        </div>

        {/* Name */}
        <div className="space-y-2">
          <Label htmlFor="name">
            Full name <span className="text-destructive">*</span>
          </Label>
          <Input
            id="name"
            value={data.name || ""}
            onChange={(e) => handleChange("name", e.target.value)}
            placeholder="John Doe"
            className={cn(errors.name && "border-destructive")}
          />
          {errors.name && (
            <p className="text-xs text-destructive">{errors.name}</p>
          )}
        </div>

        {/* Role */}
        <div className="space-y-2">
          <Label htmlFor="role">What's your role?</Label>
          <Select value={data.role || ""} onValueChange={(v) => handleChange("role", v)}>
            <SelectTrigger>
              <SelectValue placeholder="Select your role" />
            </SelectTrigger>
            <SelectContent>
              {roles.map((role) => (
                <SelectItem key={role.value} value={role.value}>
                  {role.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <NavigationButtons
        onNext={handleNext}
        onBack={onBack}
        onSkip={onSkip}
        className="max-w-sm mx-auto"
      />
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Sun, Moon, Monitor } from "lucide-react";
import { NavigationButtons } from "./navigation-buttons";
import type { OnboardingPreferences } from "../types/onboarding";

interface StepPreferencesProps {
  data: OnboardingPreferences;
  onChange: (data: OnboardingPreferences) => void;
  onNext: () => void;
  onBack: () => void;
  onSkip?: () => void;
  className?: string;
}

const themes: { id: OnboardingPreferences["theme"]; label: string; icon: typeof Sun }[] = [
  { id: "light", label: "Light", icon: Sun },
  { id: "dark", label: "Dark", icon: Moon },
  { id: "system", label: "System", icon: Monitor },
];

export function StepPreferences({
  data,
  onChange,
  onNext,
  onBack,
  onSkip,
  className,
}: StepPreferencesProps) {
  const handleThemeChange = (theme: OnboardingPreferences["theme"]) => {
    onChange({ ...data, theme });
  };

  const handleToggle = (field: keyof OnboardingPreferences, value: boolean) => {
    onChange({ ...data, [field]: value });
  };

  return (
    <div className={cn("space-y-6", className)}>
      <div className="space-y-2 text-center">
        <h2 className="text-2xl font-bold tracking-tight">Your preferences</h2>
        <p className="text-muted-foreground">
          Customize your experience. You can change these later in settings.
        </p>
      </div>

      <div className="space-y-6 max-w-sm mx-auto">
        {/* Theme selection */}
        <div className="space-y-3">
          <Label>Theme</Label>
          <div className="grid grid-cols-3 gap-3">
            {themes.map((theme) => {
              const Icon = theme.icon;
              const isSelected = data.theme === theme.id;
              return (
                <button
                  key={theme.id}
                  type="button"
                  onClick={() => handleThemeChange(theme.id)}
                  className={cn(
                    "flex flex-col items-center gap-2 p-4 rounded-lg border-2 transition-colors",
                    isSelected
                      ? "border-primary bg-primary/5"
                      : "border-muted hover:border-muted-foreground/20"
                  )}
                >
                  <Icon className={cn("size-5", isSelected && "text-primary")} />
                  <span className={cn("text-sm font-medium", isSelected && "text-primary")}>
                    {theme.label}
                  </span>
                </button>
              );
            })}
          </div>
        </div>

        {/* Notification preferences */}
        <div className="space-y-4">
          <Label>Notifications</Label>

          <div className="flex items-center justify-between p-3 rounded-lg border">
            <div className="space-y-0.5">
              <Label htmlFor="emailNotifications" className="cursor-pointer text-sm font-medium">
                Email notifications
              </Label>
              <p className="text-xs text-muted-foreground">
                Receive updates about your account
              </p>
            </div>
            <Switch
              id="emailNotifications"
              checked={data.emailNotifications}
              onCheckedChange={(v) => handleToggle("emailNotifications", v)}
            />
          </div>

          <div className="flex items-center justify-between p-3 rounded-lg border">
            <div className="space-y-0.5">
              <Label htmlFor="weeklyDigest" className="cursor-pointer text-sm font-medium">
                Weekly digest
              </Label>
              <p className="text-xs text-muted-foreground">
                Get a summary of your activity
              </p>
            </div>
            <Switch
              id="weeklyDigest"
              checked={data.weeklyDigest}
              onCheckedChange={(v) => handleToggle("weeklyDigest", v)}
            />
          </div>
        </div>
      </div>

      <NavigationButtons
        onNext={onNext}
        onBack={onBack}
        onSkip={onSkip}
        className="max-w-sm mx-auto"
      />
    </div>
  );
}

"use client";

import { useState } from "react";
import { cn } from "@/lib/utils";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { User, Users, Building, Building2 } from "lucide-react";
import { NavigationButtons } from "./navigation-buttons";
import type { OnboardingWorkspace } from "../types/onboarding";

interface StepWorkspaceProps {
  data: OnboardingWorkspace;
  onChange: (data: OnboardingWorkspace) => void;
  onNext: () => void;
  onBack: () => void;
  onSkip?: () => void;
  isLoading?: boolean;
  className?: string;
}

const teamSizes: { id: OnboardingWorkspace["teamSize"]; label: string; description: string; icon: typeof User }[] = [
  { id: "just-me", label: "Just me", description: "Personal workspace", icon: User },
  { id: "small", label: "2-10", description: "Small team", icon: Users },
  { id: "medium", label: "11-50", description: "Growing team", icon: Building },
  { id: "large", label: "50+", description: "Large organization", icon: Building2 },
];

const industries = [
  { value: "technology", label: "Technology" },
  { value: "healthcare", label: "Healthcare" },
  { value: "finance", label: "Finance" },
  { value: "education", label: "Education" },
  { value: "ecommerce", label: "E-commerce" },
  { value: "agency", label: "Agency" },
  { value: "other", label: "Other" },
];

export function StepWorkspace({
  data,
  onChange,
  onNext,
  onBack,
  onSkip,
  isLoading,
  className,
}: StepWorkspaceProps) {
  const [errors, setErrors] = useState<Record<string, string>>({});

  const handleChange = (field: keyof OnboardingWorkspace, value: string) => {
    onChange({ ...data, [field]: value });
    if (errors[field]) {
      setErrors((prev) => ({ ...prev, [field]: "" }));
    }
  };

  const validate = (): boolean => {
    const newErrors: Record<string, string> = {};
    if (!data.workspaceName?.trim()) {
      newErrors.workspaceName = "Workspace name is required";
    }
    if (!data.teamSize) {
      newErrors.teamSize = "Please select a team size";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validate()) {
      onNext();
    }
  };

  return (
    <div className={cn("space-y-6", className)}>
      <div className="space-y-2 text-center">
        <h2 className="text-2xl font-bold tracking-tight">Create your workspace</h2>
        <p className="text-muted-foreground">
          Set up your workspace to collaborate with your team.
        </p>
      </div>

      <div className="space-y-6 max-w-sm mx-auto">
        {/* Workspace name */}
        <div className="space-y-2">
          <Label htmlFor="workspaceName">
            Workspace name <span className="text-destructive">*</span>
          </Label>
          <Input
            id="workspaceName"
            value={data.workspaceName || ""}
            onChange={(e) => handleChange("workspaceName", e.target.value)}
            placeholder="My Workspace"
            className={cn(errors.workspaceName && "border-destructive")}
          />
          {errors.workspaceName && (
            <p className="text-xs text-destructive">{errors.workspaceName}</p>
          )}
        </div>

        {/* Team size */}
        <div className="space-y-3">
          <Label>
            Team size <span className="text-destructive">*</span>
          </Label>
          <div className="grid grid-cols-2 gap-3">
            {teamSizes.map((size) => {
              const Icon = size.icon;
              const isSelected = data.teamSize === size.id;
              return (
                <button
                  key={size.id}
                  type="button"
                  onClick={() => handleChange("teamSize", size.id)}
                  className={cn(
                    "flex flex-col items-center gap-1 p-4 rounded-lg border-2 transition-colors",
                    isSelected
                      ? "border-primary bg-primary/5"
                      : "border-muted hover:border-muted-foreground/20",
                    errors.teamSize && "border-destructive/50"
                  )}
                >
                  <Icon className={cn("size-5", isSelected && "text-primary")} />
                  <span className={cn("text-sm font-medium", isSelected && "text-primary")}>
                    {size.label}
                  </span>
                  <span className="text-xs text-muted-foreground">{size.description}</span>
                </button>
              );
            })}
          </div>
          {errors.teamSize && (
            <p className="text-xs text-destructive">{errors.teamSize}</p>
          )}
        </div>

        {/* Industry */}
        <div className="space-y-2">
          <Label htmlFor="industry">Industry (optional)</Label>
          <Select value={data.industry || ""} onValueChange={(v) => handleChange("industry", v)}>
            <SelectTrigger>
              <SelectValue placeholder="Select your industry" />
            </SelectTrigger>
            <SelectContent>
              {industries.map((industry) => (
                <SelectItem key={industry.value} value={industry.value}>
                  {industry.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <NavigationButtons
        onNext={handleNext}
        onBack={onBack}
        onSkip={onSkip}
        nextLabel="Create workspace"
        isLoading={isLoading}
        className="max-w-sm mx-auto"
      />
    </div>
  );
}

"use client";

import { useEffect } from "react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { CheckCircle, ArrowRight } from "lucide-react";
import confetti from "canvas-confetti";

interface StepCompleteProps {
  workspaceName: string;
  showConfetti?: boolean;
  onFinish: () => void;
  className?: string;
}

export function StepComplete({
  workspaceName,
  showConfetti = true,
  onFinish,
  className,
}: StepCompleteProps) {
  useEffect(() => {
    if (showConfetti) {
      // Fire confetti
      const duration = 2000;
