"use client";

import { useState, useEffect } from "react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { ArrowRight, ArrowLeft, Loader2 } from "lucide-react";
import confetti from "canvas-confetti";
import {
  StepProgress,
  StepCompanyType,
  StepCompanySize,
  StepFunding,
  StepUseCase,
  StepComplete,
} from "./components";
import { LogoIcon } from "@/registry/blocks/social-icons/icons";
import type {
  OnboardingFormData,
  OnboardingProps,
  CompanyType,
  CompanySize,
  FundingAmount,
  UseCase,
} from "./types/onboarding";

// =============================================================================
// Initial State
// =============================================================================

const initialFormData: OnboardingFormData = {
  companyType: null,
  companySize: null,
  fundingAmount: null,
  useCases: [],
  fullName: "",
  role: "",
};

const TOTAL_STEPS = 5;

// =============================================================================
// Onboarding Component
// =============================================================================

export default function Onboarding({
  logo,
  onComplete,
  className,
}: OnboardingProps) {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState<OnboardingFormData>(initialFormData);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isComplete, setIsComplete] = useState(false);

  // Trigger confetti on completion
  useEffect(() => {
    if (isComplete) {
      const duration = 3000;
      const animationEnd = Date.now() + duration;

      const interval = setInterval(() => {
        const timeLeft = animationEnd - Date.now();
        if (timeLeft <= 0) {
          return clearInterval(interval);
        }

        confetti({
          particleCount: 3,
          angle: 60,
          spread: 55,
          origin: { x: 0 },
          colors: ["#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6"],
        });
        confetti({
          particleCount: 3,
          angle: 120,
          spread: 55,
          origin: { x: 1 },
          colors: ["#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6"],
        });
      }, 150);

      return () => clearInterval(interval);
    }
  }, [isComplete]);

  // Check if current step is valid
  const isStepValid = () => {
    switch (currentStep) {
      case 1:
        return formData.companyType !== null;
      case 2:
        return formData.companySize !== null;
      case 3:
        return formData.fundingAmount !== null;
      case 4:
        return formData.useCases.length > 0;
      default:
        return true;
    }
  };

  // Handle next step
  const handleNext = async () => {
    if (currentStep < TOTAL_STEPS) {
      setCurrentStep((prev) => prev + 1);
    } else {
      // Complete onboarding
      setIsSubmitting(true);
      try {
        if (onComplete) {
          await onComplete(formData);
        }
        setIsComplete(true);
      } catch (error) {
        console.error("Failed to complete onboarding:", error);
      } finally {
        setIsSubmitting(false);
      }
    }
  };

  // Handle previous step
  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep((prev) => prev - 1);
    }
  };

  // Update form data
  const updateFormData = (updates: Partial<OnboardingFormData>) => {
    setFormData((prev) => ({ ...prev, ...updates }));
  };

  // Render current step content
  const renderStepContent = () => {
    if (isComplete) {
      return <StepComplete />;
    }

    switch (currentStep) {
      case 1:
        return (
          <StepCompanyType
            value={formData.companyType}
            onChange={(v) => updateFormData({ companyType: v })}
          />
        );
      case 2:
        return (
          <StepCompanySize
            value={formData.companySize}
            onChange={(v) => updateFormData({ companySize: v })}
          />
        );
      case 3:
        return (
          <StepFunding
            value={formData.fundingAmount}
            onChange={(v) => updateFormData({ fundingAmount: v })}
          />
        );
      case 4:
        return (
          <StepUseCase
            values={formData.useCases}
            onChange={(v) => updateFormData({ useCases: v })}
          />
        );
      case 5:
        return (
          <div className="space-y-6">
            <div className="space-y-2">
              <h1 className="text-3xl font-bold tracking-tight">
                Almost there!
              </h1>
              <p className="text-muted-foreground">
                Review your selections and complete the setup.
              </p>
            </div>

            <div className="space-y-3 text-sm">
              <div className="flex justify-between py-2 border-b">
                <span className="text-muted-foreground">Company Type</span>
                <span className="font-medium capitalize">
                  {formData.companyType?.replace(/_/g, " ") || "Not selected"}
                </span>
              </div>
              <div className="flex justify-between py-2 border-b">
                <span className="text-muted-foreground">Company Size</span>
                <span className="font-medium">
                  {formData.companySize || "Not selected"}
                </span>
              </div>
              <div className="flex justify-between py-2 border-b">
                <span className="text-muted-foreground">Funding</span>
                <span className="font-medium capitalize">
                  {formData.fundingAmount?.replace(/_/g, " ") || "Not selected"}
                </span>
              </div>
              <div className="flex justify-between py-2">
                <span className="text-muted-foreground">Use Cases</span>
                <span className="font-medium capitalize text-right">
                  {formData.useCases.length > 0
                    ? formData.useCases.map((u) => u.replace(/_/g, " ")).join(", ")
                    : "None selected"}
                </span>
              </div>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  // Default logo
  const displayLogo = logo || <LogoIcon className="size-10" />;

  return (
    <div
      className={cn(
        "min-h-screen flex items-start justify-center bg-muted/30 p-4 pt-16 md:pt-24",
        className
      )}
    >
      <Card className="w-full max-w-xl shadow-lg">
        <CardContent className="p-8">
          {/* Logo */}
          <div className="mb-8">{displayLogo}</div>

          {/* Progress */}
          {!isComplete && (
            <StepProgress
              currentStep={currentStep}
              totalSteps={TOTAL_STEPS}
              className="mb-8"
            />
          )}

          {/* Content - Fixed height container */}
          <div className="min-h-[320px] mb-8">{renderStepContent()}</div>

          {/* Navigation */}
          {!isComplete && (
            <div className="flex items-center justify-between">
              <Button
                variant="ghost"
                onClick={handlePrevious}
                disabled={currentStep === 1}
                className={cn(currentStep === 1 && "invisible")}
              >
                <ArrowLeft className="size-4 mr-2" />
                Back
              </Button>

              <Button
                onClick={handleNext}
                disabled={!isStepValid() || isSubmitting}
              >
                {isSubmitting ? (
                  <Loader2 className="size-4 mr-2 animate-spin" />
                ) : null}
                {currentStep === TOTAL_STEPS ? "Complete Setup" : "Continue"}
                {currentStep < TOTAL_STEPS && !isSubmitting && (
                  <ArrowRight className="size-4 ml-2" />
                )}
              </Button>
            </div>
          )}

          {/* Complete action */}
          {isComplete && (
            <div className="flex justify-center">
              <Button size="lg">
                Go to Dashboard
                <ArrowRight className="size-4 ml-2" />
              </Button>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

// =============================================================================
// Onboarding Types (Modern)
// =============================================================================

export interface OnboardingStep {
  id: string;
  title: string;
  description: string;
}

export type CompanyType =
  | "bootstrapped_startup"
  | "software_agency"
  | "freelancer"
  | "ecommerce"
  | "consulting"
  | "university"
  | "tech_enterprise"
  | "pre_seed"
  | "seed"
  | "vc_firm"
  | "other";

export type CompanySize = "1-10" | "11-50" | "51-200" | "201-500" | "500+";

export type FundingAmount =
  | "no_funding"
  | "less_than_3m"
  | "less_than_10m"
  | "less_than_50m"
  | "more_than_50m";

export type UseCase =
  | "project_management"
  | "documentation"
  | "team_collaboration"
  | "product_roadmap"
  | "engineering"
  | "design"
  | "marketing"
  | "sales"
  | "other";

export interface OnboardingFormData {
  // Step 1: Company type
  companyType: CompanyType | null;
  // Step 2: Company size
  companySize: CompanySize | null;
  // Step 3: Funding
  fundingAmount: FundingAmount | null;
  // Step 4: Use case
  useCases: UseCase[];
  // Step 5: Personal info
  fullName: string;
  role: string;
}

export interface OnboardingProps {
  steps?: OnboardingStep[];
  logo?: React.ReactNode;
  onComplete?: (data: OnboardingFormData) => Promise<void>;
  className?: string;
}

export interface StepProgressProps {
  currentStep: number;
  totalSteps: number;
}

export interface ChipSelectProps {
  options: { value: string; label: string }[];
  value: string | null;
  onChange: (value: string) => void;
  multiSelect?: boolean;
  selectedValues?: string[];
  onMultiChange?: (values: string[]) => void;
}

// Components
export { StepProgress } from "./step-progress";
export { ChipSelect } from "./chip-select";
export { StepCompanyType } from "./step-company-type";
export { StepCompanySize } from "./step-company-size";
export { StepFunding } from "./step-funding";
export { StepUseCase } from "./step-use-case";
export { StepComplete } from "./step-complete";

// Re-export types
export type {
  OnboardingStep,
  CompanyType,
  CompanySize,
  FundingAmount,
  UseCase,
  OnboardingFormData,
  OnboardingProps,
  StepProgressProps,
  ChipSelectProps,
} from "../types/onboarding";

"use client";

import { cn } from "@/lib/utils";

interface StepProgressProps {
  currentStep: number;
  totalSteps: number;
  className?: string;
}

export function StepProgress({
  currentStep,
  totalSteps,
  className,
}: StepProgressProps) {
  return (
    <div className={cn("flex gap-2", className)}>
      {Array.from({ length: totalSteps }).map((_, index) => (
        <div
          key={index}
          className={cn(
            "h-1.5 flex-1 rounded-full transition-colors duration-300",
            index < currentStep ? "bg-foreground" : "bg-muted"
          )}
        />
      ))}
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";

interface ChipSelectProps {
  options: { value: string; label: string }[];
  value?: string | null;
  onChange?: (value: string) => void;
  multiSelect?: boolean;
  selectedValues?: string[];
  onMultiChange?: (values: string[]) => void;
  className?: string;
}

export function ChipSelect({
  options,
  value,
  onChange,
  multiSelect = false,
  selectedValues = [],
  onMultiChange,
  className,
}: ChipSelectProps) {
  const handleClick = (optionValue: string) => {
    if (multiSelect && onMultiChange) {
      const newValues = selectedValues.includes(optionValue)
        ? selectedValues.filter((v) => v !== optionValue)
        : [...selectedValues, optionValue];
      onMultiChange(newValues);
    } else if (onChange) {
      onChange(optionValue);
    }
  };

  const isSelected = (optionValue: string) => {
    if (multiSelect) {
      return selectedValues.includes(optionValue);
    }
    return value === optionValue;
  };

  return (
    <div className={cn("flex flex-wrap gap-2", className)}>
      {options.map((option) => (
        <button
          key={option.value}
          type="button"
          onClick={() => handleClick(option.value)}
          className={cn(
            "px-4 py-2.5 rounded-lg text-sm font-medium transition-all duration-200",
            "border hover:border-foreground/50",
            isSelected(option.value)
              ? "bg-foreground text-background border-foreground"
              : "bg-background text-foreground border-border hover:bg-muted/50"
          )}
        >
          {option.label}
        </button>
      ))}
    </div>
  );
}

