"use client";

import { useState } from "react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { TooltipProvider } from "@/components/ui/tooltip";
import {
  SidebarHeader,
  SidebarNav,
  SidebarUserMenu,
  MobileSidebar,
} from "./components";
import {
  LayoutDashboard,
  FolderKanban,
  Users,
  Settings,
  Bell,
  FileText,
  BarChart3,
  Calendar,
  MessageSquare,
  HelpCircle,
  Menu,
} from "lucide-react";
import type { SidebarProps, NavGroup, SidebarUser } from "./types/sidebar";

// =============================================================================
// Demo Data
// =============================================================================

const demoUser: SidebarUser = {
  id: "user_1",
  name: "Sarah Chen",
  email: "sarah@acme.com",
  avatar: "https://api.dicebear.com/7.x/avataaars/svg?seed=sarah",
  role: "Product Manager",
};

const demoNavigation: NavGroup[] = [
  {
    id: "main",
    items: [
      {
        id: "dashboard",
        label: "Dashboard",
        href: "/dashboard",
        icon: <LayoutDashboard className="size-5" />,
      },
      {
        id: "projects",
        label: "Projects",
        href: "/projects",
        icon: <FolderKanban className="size-5" />,
        badge: 3,
      },
      {
        id: "analytics",
        label: "Analytics",
        href: "/analytics",
        icon: <BarChart3 className="size-5" />,
      },
    ],
  },
  {
    id: "workspace",
    label: "Workspace",
    collapsible: true,
    defaultOpen: true,
    items: [
      {
        id: "calendar",
        label: "Calendar",
        href: "/calendar",
        icon: <Calendar className="size-5" />,
      },
      {
        id: "documents",
        label: "Documents",
        href: "/documents",
        icon: <FileText className="size-5" />,
      },
      {
        id: "messages",
        label: "Messages",
        href: "/messages",
        icon: <MessageSquare className="size-5" />,
        badge: 12,
        badgeVariant: "destructive",
      },
    ],
  },
  {
    id: "team",
    label: "Team",
    collapsible: true,
    defaultOpen: true,
    items: [
      {
        id: "members",
        label: "Members",
        href: "/team/members",
        icon: <Users className="size-5" />,
      },
      {
        id: "notifications",
        label: "Notifications",
        href: "/notifications",
        icon: <Bell className="size-5" />,
      },
    ],
  },
  {
    id: "support",
    label: "Support",
    collapsible: true,
    defaultOpen: false,
    items: [
      {
        id: "settings",
        label: "Settings",
        href: "/settings",
        icon: <Settings className="size-5" />,
      },
      {
        id: "help",
        label: "Help & Support",
        href: "/help",
        icon: <HelpCircle className="size-5" />,
      },
    ],
  },
];

// =============================================================================
// Sidebar Component
// =============================================================================

export default function Sidebar({
  navigation: externalNavigation,
  user: externalUser,
  logo,
  logoText = "Acme Inc",
  collapsed: externalCollapsed,
  onCollapsedChange,
  onLogout,
  onSettings,
  onProfile,
  activeItemId: externalActiveItemId,
  onNavigate,
  className,
}: SidebarProps) {
  const [internalCollapsed, setInternalCollapsed] = useState(false);
  const [internalActiveItemId, setInternalActiveItemId] = useState("dashboard");
  const [mobileOpen, setMobileOpen] = useState(false);

  // Use external state if provided
  const navigation = externalNavigation ?? demoNavigation;
  const user = externalUser ?? demoUser;
  const collapsed = externalCollapsed ?? internalCollapsed;
  const activeItemId = externalActiveItemId ?? internalActiveItemId;

  const handleCollapsedChange = (value: boolean) => {
    if (onCollapsedChange) {
      onCollapsedChange(value);
    } else {
      setInternalCollapsed(value);
    }
  };

  const handleNavigate = (item: { id: string; href?: string }) => {
    if (onNavigate) {
      onNavigate(item as any);
    } else {
      setInternalActiveItemId(item.id);
      console.log("Navigate to:", item.href || item.id);
    }
  };

  const handleLogout = () => {
    if (onLogout) {
      onLogout();
    } else {
      console.log("Logout clicked");
    }
  };

  const handleSettings = () => {
    if (onSettings) {
      onSettings();
    } else {
      setInternalActiveItemId("settings");
      console.log("Settings clicked");
    }
  };

  const handleProfile = () => {
    if (onProfile) {
      onProfile();
    } else {
      console.log("Profile clicked");
    }
  };

  return (
    <TooltipProvider>
      {/* Desktop Sidebar */}
      <aside
        className={cn(
          "hidden lg:flex flex-col h-screen bg-background border-r transition-all duration-300 relative",
          collapsed ? "w-16" : "w-64",
          className
        )}
      >
        <SidebarHeader
          logo={logo}
          logoText={logoText}
          collapsed={collapsed}
          onCollapsedChange={handleCollapsedChange}
        />

        <SidebarNav
          groups={navigation}
          collapsed={collapsed}
          activeItemId={activeItemId}
          onNavigate={handleNavigate}
        />

        <SidebarUserMenu
          user={user}
          collapsed={collapsed}
          onLogout={handleLogout}
          onSettings={handleSettings}
          onProfile={handleProfile}
        />
      </aside>

      {/* Mobile Header & Sidebar */}
      <div className="lg:hidden">
        {/* Mobile Header */}
        <div className="flex items-center justify-between h-16 px-4 border-b bg-background">
          <div className="flex items-center gap-3">
            <Button
              variant="ghost"
              size="icon"
              className="size-9"
              onClick={() => setMobileOpen(true)}
              aria-label="Open menu"
            >
              <Menu className="size-5" />
            </Button>
            <div className="flex items-center gap-2">
              <div className="flex size-8 items-center justify-center rounded-lg bg-primary text-primary-foreground">
                {logo || (
                  <svg
                    viewBox="0 0 24 24"
                    className="size-5"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  >
                    <path d="M12 2L2 7l10 5 10-5-10-5Z" />
                    <path d="M2 17l10 5 10-5" />
                    <path d="M2 12l10 5 10-5" />
                  </svg>
                )}
              </div>
              <span className="font-semibold">{logoText}</span>
            </div>
          </div>
        </div>

        {/* Mobile Sidebar Sheet */}
        <MobileSidebar
          open={mobileOpen}
          onOpenChange={setMobileOpen}
          navigation={navigation}
          user={user}
          logo={logo}
          logoText={logoText}
          activeItemId={activeItemId}
          onNavigate={handleNavigate}
          onLogout={handleLogout}
          onSettings={handleSettings}
          onProfile={handleProfile}
        />
      </div>

      {/* Demo Content Area */}
      <div className="flex-1 flex flex-col min-h-screen lg:min-h-0">
        <main className="flex-1 p-6">
          <div className="max-w-4xl mx-auto">
            <div className="mb-8">
              <h1 className="text-3xl font-bold tracking-tight">
                {navigation
                  .flatMap((g) => g.items)
                  .find((i) => i.id === activeItemId)?.label || "Dashboard"}
              </h1>
              <p className="text-muted-foreground mt-1">
                Welcome back, {user.name.split(" ")[0]}
              </p>
            </div>

            {/* Demo Stats */}
            <div className="grid gap-4 md:grid-cols-3 mb-8">
              {[
                { label: "Total Projects", value: "12", change: "+2 this week" },
                { label: "Active Tasks", value: "24", change: "8 due today" },
                { label: "Team Members", value: "8", change: "2 online" },
              ].map((stat) => (
                <div
                  key={stat.label}
                  className="rounded-xl border bg-card p-6 shadow-sm"
                >
                  <p className="text-sm text-muted-foreground">{stat.label}</p>
                  <p className="text-3xl font-bold mt-1">{stat.value}</p>
                  <p className="text-xs text-muted-foreground mt-1">
                    {stat.change}
                  </p>
                </div>
              ))}
            </div>

            {/* Demo Activity */}
            <div className="rounded-xl border bg-card shadow-sm">
              <div className="p-6 border-b">
                <h2 className="font-semibold">Recent Activity</h2>
              </div>
              <div className="divide-y">
                {[
                  { action: "Created new project", time: "2 minutes ago" },
                  { action: "Completed task: Design review", time: "1 hour ago" },
                  { action: "Added team member", time: "3 hours ago" },
                  { action: "Updated project settings", time: "Yesterday" },
                ].map((activity, idx) => (
                  <div key={idx} className="px-6 py-4 flex items-center justify-between">
                    <span className="text-sm">{activity.action}</span>
                    <span className="text-xs text-muted-foreground">
                      {activity.time}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </main>
      </div>
    </TooltipProvider>
  );
}

// =============================================================================
// Sidebar Types
// =============================================================================

export interface NavItem {
  id: string;
  label: string;
  href?: string;
  icon?: React.ReactNode;
  badge?: string | number;
  badgeVariant?: "default" | "secondary" | "destructive" | "outline";
  isActive?: boolean;
  onClick?: () => void;
}

export interface NavGroup {
  id: string;
  label?: string;
  items: NavItem[];
  collapsible?: boolean;
  defaultOpen?: boolean;
}

export interface SidebarUser {
  id: string;
  name: string;
  email: string;
  avatar?: string;
  role?: string;
}

export interface SidebarProps {
  /** Navigation groups */
  navigation?: NavGroup[];
  /** Current user */
  user?: SidebarUser;
  /** Logo element */
  logo?: React.ReactNode;
  /** Logo text */
  logoText?: string;
  /** Whether the sidebar is collapsed */
  collapsed?: boolean;
  /** Callback when collapse state changes */
  onCollapsedChange?: (collapsed: boolean) => void;
  /** Callback when user clicks logout */
  onLogout?: () => void;
  /** Callback when user clicks settings */
  onSettings?: () => void;
  /** Callback when user clicks profile */
  onProfile?: () => void;
  /** Active item ID */
  activeItemId?: string;
  /** Callback when navigation item is clicked */
  onNavigate?: (item: NavItem) => void;
  /** Additional class names */
  className?: string;
}

export interface SidebarHeaderProps {
  logo?: React.ReactNode;
  logoText?: string;
  collapsed?: boolean;
  onCollapsedChange?: (collapsed: boolean) => void;
}

export interface SidebarNavProps {
  groups: NavGroup[];
  collapsed?: boolean;
  activeItemId?: string;
  onNavigate?: (item: NavItem) => void;
}

export interface SidebarUserMenuProps {
  user: SidebarUser;
  collapsed?: boolean;
  onLogout?: () => void;
  onSettings?: () => void;
  onProfile?: () => void;
}

export interface MobileSidebarProps extends SidebarProps {
  open?: boolean;
  onOpenChange?: (open: boolean) => void;
}

// Components
export { SidebarHeader } from "./sidebar-header";
export { SidebarNav } from "./sidebar-nav";
export { SidebarUserMenu } from "./sidebar-user-menu";
export { MobileSidebar } from "./mobile-sidebar";

// Re-export types
export type {
  NavItem,
  NavGroup,
  SidebarUser,
  SidebarProps,
  SidebarHeaderProps,
  SidebarNavProps,
  SidebarUserMenuProps,
  MobileSidebarProps,
} from "../types/sidebar";

"use client";

import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { PanelLeftClose, PanelLeft } from "lucide-react";
import type { SidebarHeaderProps } from "../types/sidebar";

export function SidebarHeader({
  logo,
  logoText = "Acme Inc",
  collapsed,
  onCollapsedChange,
}: SidebarHeaderProps) {
  return (
    <div className="flex h-16 items-center justify-between px-4 border-b">
      <div className={cn("flex items-center gap-3 overflow-hidden", collapsed && "justify-center w-full")}>
        <div className="flex size-8 items-center justify-center rounded-lg bg-primary text-primary-foreground shrink-0">
          {logo || (
            <svg
              viewBox="0 0 24 24"
              className="size-5"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <path d="M12 2L2 7l10 5 10-5-10-5Z" />
              <path d="M2 17l10 5 10-5" />
              <path d="M2 12l10 5 10-5" />
            </svg>
          )}
        </div>
        {!collapsed && (
          <span className="font-semibold text-lg truncate">{logoText}</span>
        )}
      </div>
      {!collapsed && onCollapsedChange && (
        <Button
          variant="ghost"
          size="icon"
          className="size-8 shrink-0"
          onClick={() => onCollapsedChange(true)}
          aria-label="Collapse sidebar"
        >
          <PanelLeftClose className="size-4" />
        </Button>
      )}
      {collapsed && onCollapsedChange && (
        <Button
          variant="ghost"
          size="icon"
          className="size-8 absolute -right-3 top-6 bg-background border shadow-sm rounded-full z-10"
          onClick={() => onCollapsedChange(false)}
          aria-label="Expand sidebar"
        >
          <PanelLeft className="size-4" />
        </Button>
      )}
    </div>
  );
}

"use client";

import { useState } from "react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible";
import { ChevronRight } from "lucide-react";
import type { SidebarNavProps, NavGroup, NavItem } from "../types/sidebar";

interface NavItemButtonProps {
  item: NavItem;
  collapsed?: boolean;
  isActive?: boolean;
  onClick?: () => void;
}

function NavItemButton({ item, collapsed, isActive, onClick }: NavItemButtonProps) {
  const content = (
    <Button
      variant="ghost"
      className={cn(
        "w-full justify-start gap-3 h-10 px-3 font-normal",
        collapsed && "justify-center px-0",
        isActive && "bg-accent text-accent-foreground font-medium"
      )}
      onClick={onClick}
    >
      {item.icon && (
        <span className={cn("size-5 shrink-0", isActive && "text-primary")}>
          {item.icon}
        </span>
      )}
      {!collapsed && (
        <>
          <span className="truncate flex-1 text-left">{item.label}</span>
          {item.badge !== undefined && (
            <Badge
              variant={item.badgeVariant || "secondary"}
              className="ml-auto h-5 px-1.5 text-xs"
            >
              {item.badge}
            </Badge>
          )}
        </>
      )}
    </Button>
  );

  if (collapsed) {
    return (
      <Tooltip delayDuration={0}>
        <TooltipTrigger asChild>{content}</TooltipTrigger>
        <TooltipContent side="right" className="flex items-center gap-2">
          {item.label}
          {item.badge !== undefined && (
            <Badge variant={item.badgeVariant || "secondary"} className="h-5 px-1.5 text-xs">
              {item.badge}
            </Badge>
          )}
        </TooltipContent>
      </Tooltip>
    );
  }

  return content;
}

interface NavGroupSectionProps {
  group: NavGroup;
  collapsed?: boolean;
  activeItemId?: string;
  onNavigate?: (item: NavItem) => void;
}

function NavGroupSection({ group, collapsed, activeItemId, onNavigate }: NavGroupSectionProps) {
  const [isOpen, setIsOpen] = useState(group.defaultOpen !== false);

  // Non-collapsible group
  if (!group.collapsible || collapsed) {
    return (
      <div className="space-y-1">
        {group.label && !collapsed && (
          <div className="px-3 py-2">
            <span className="text-xs font-medium text-muted-foreground uppercase tracking-wider">
              {group.label}
            </span>
          </div>
        )}
        {group.items.map((item) => (
          <NavItemButton
            key={item.id}
            item={item}
            collapsed={collapsed}
            isActive={item.id === activeItemId || item.isActive}
            onClick={() => {
              item.onClick?.();
              onNavigate?.(item);
            }}
          />
        ))}
      </div>
    );
  }

  // Collapsible group
  return (
    <Collapsible open={isOpen} onOpenChange={setIsOpen}>
      <CollapsibleTrigger asChild>
        <Button
          variant="ghost"
          className="w-full justify-between h-9 px-3 font-normal text-muted-foreground hover:text-foreground"
        >
          <span className="text-xs font-medium uppercase tracking-wider">
            {group.label}
          </span>
          <ChevronRight
            className={cn(
              "size-4 transition-transform duration-200",
              isOpen && "rotate-90"
            )}
          />
        </Button>
      </CollapsibleTrigger>
      <CollapsibleContent className="space-y-1 pt-1">
        {group.items.map((item) => (
          <NavItemButton
            key={item.id}
            item={item}
            collapsed={collapsed}
            isActive={item.id === activeItemId || item.isActive}
            onClick={() => {
              item.onClick?.();
              onNavigate?.(item);
            }}
          />
        ))}
      </CollapsibleContent>
    </Collapsible>
  );
}

export function SidebarNav({ groups, collapsed, activeItemId, onNavigate }: SidebarNavProps) {
  return (
    <nav className="flex-1 overflow-y-auto py-4 px-2 space-y-6">
      {groups.map((group) => (
        <NavGroupSection
          key={group.id}
          group={group}
          collapsed={collapsed}
          activeItemId={activeItemId}
          onNavigate={onNavigate}
        />
      ))}
    </nav>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { ChevronsUpDown, User, Settings, LogOut, Moon, Sun } from "lucide-react";
import { useTheme } from "next-themes";
import type { SidebarUserMenuProps } from "../types/sidebar";

export function SidebarUserMenu({
  user,
  collapsed,
  onLogout,
  onSettings,
  onProfile,
}: SidebarUserMenuProps) {
  const { theme, setTheme } = useTheme();

  const initials = user.name
    .split(" ")
    .map((n) => n[0])
    .join("")
    .toUpperCase()
    .slice(0, 2);

  const menuContent = (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          variant="ghost"
          className={cn(
            "w-full h-14 justify-start gap-3 px-3",
            collapsed && "justify-center px-0"
          )}
        >
          <Avatar className="size-9 shrink-0">
            <AvatarImage src={user.avatar} alt={user.name} />
            <AvatarFallback className="bg-gradient-to-br from-primary/80 to-primary text-primary-foreground text-sm">
              {initials}
            </AvatarFallback>
          </Avatar>
          {!collapsed && (
            <>
              <div className="flex-1 text-left overflow-hidden">
                <p className="text-sm font-medium truncate">{user.name}</p>
                <p className="text-xs text-muted-foreground truncate">
                  {user.email}
                </p>
              </div>
              <ChevronsUpDown className="size-4 text-muted-foreground shrink-0" />
            </>
          )}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent
        className="w-56"
        align={collapsed ? "center" : "end"}
        side={collapsed ? "right" : "top"}
        sideOffset={8}
      >
        <DropdownMenuLabel className="font-normal">
          <div className="flex flex-col space-y-1">
            <p className="text-sm font-medium">{user.name}</p>
            <p className="text-xs text-muted-foreground">{user.email}</p>
            {user.role && (
              <p className="text-xs text-muted-foreground">{user.role}</p>
            )}
          </div>
        </DropdownMenuLabel>
        <DropdownMenuSeparator />
        <DropdownMenuGroup>
          <DropdownMenuItem onClick={onProfile}>
            <User className="mr-2 size-4" />
            Profile
          </DropdownMenuItem>
          <DropdownMenuItem onClick={onSettings}>
            <Settings className="mr-2 size-4" />
            Settings
          </DropdownMenuItem>
        </DropdownMenuGroup>
        <DropdownMenuSeparator />
        <DropdownMenuItem
          onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
        >
          {theme === "dark" ? (
            <Sun className="mr-2 size-4" />
          ) : (
            <Moon className="mr-2 size-4" />
          )}
          {theme === "dark" ? "Light mode" : "Dark mode"}
        </DropdownMenuItem>
        <DropdownMenuSeparator />
        <DropdownMenuItem onClick={onLogout} className="text-destructive focus:text-destructive">
          <LogOut className="mr-2 size-4" />
          Log out
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );

  if (collapsed) {
    return (
      <div className="border-t p-2">
        <Tooltip delayDuration={0}>
          <TooltipTrigger asChild>{menuContent}</TooltipTrigger>
          <TooltipContent side="right">
            <p>{user.name}</p>
          </TooltipContent>
        </Tooltip>
      </div>
    );
  }

  return <div className="border-t p-2">{menuContent}</div>;
}

"use client";

import {
  Sheet,
  SheetContent,
  SheetHeader,
  SheetTitle,
} from "@/components/ui/sheet";
import { SidebarNav } from "./sidebar-nav";
import { SidebarUserMenu } from "./sidebar-user-menu";
import type { MobileSidebarProps } from "../types/sidebar";

export function MobileSidebar({
  open,
  onOpenChange,
  navigation = [],
  user,
  logo,
  logoText = "Acme Inc",
  activeItemId,
  onNavigate,
  onLogout,
  onSettings,
  onProfile,
}: MobileSidebarProps) {
  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent side="left" className="w-72 p-0 flex flex-col">
        <SheetHeader className="h-16 flex flex-row items-center gap-3 px-4 border-b">
          <div className="flex size-8 items-center justify-center rounded-lg bg-primary text-primary-foreground shrink-0">
            {logo || (
              <svg
                viewBox="0 0 24 24"
                className="size-5"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path d="M12 2L2 7l10 5 10-5-10-5Z" />
                <path d="M2 17l10 5 10-5" />
                <path d="M2 12l10 5 10-5" />
              </svg>
            )}
          </div>
          <SheetTitle className="font-semibold text-lg">{logoText}</SheetTitle>
        </SheetHeader>

        <SidebarNav
          groups={navigation}
          collapsed={false}
          activeItemId={activeItemId}
          onNavigate={(item) => {
            onNavigate?.(item);
            onOpenChange?.(false);
          }}
        />

        {user && (
          <SidebarUserMenu
            user={user}
            collapsed={false}
            onLogout={onLogout}
            onSettings={onSettings}
            onProfile={onProfile}
          />
        )}
      </SheetContent>
    </Sheet>
  );
}
