// ============================================================================
// Column Definitions
// ============================================================================

const statusConfig: Record<User["status"], { label: string; className: string }> = {
  active: { label: "Active", className: "bg-emerald-50 text-emerald-700 dark:bg-emerald-950 dark:text-emerald-300" },
