  },
  {
    id: "createdAt",
    header: "Created",
    accessorKey: "createdAt",
    sortable: true,
    cell: (value) => new Date(value as string).toLocaleDateString(),
  },
];

// ============================================================================
// Demo Data Fetcher
// ============================================================================

async function fetchMockData(params: FetchTableDataParams): Promise<TableDataResult<User>> {
  // Simulate network delay
  await new Promise((resolve) => setTimeout(resolve, 500));

  let filtered = [...mockUsers];

  // Apply search
  if (params.search) {
    const searchLower = params.search.toLowerCase();
    filtered = filtered.filter(
      (user) =>
        user.name.toLowerCase().includes(searchLower) ||
        user.email.toLowerCase().includes(searchLower)
    );
  }

  // Apply filters
  if (params.filters && params.filters.length > 0) {
    params.filters.forEach((filter) => {
      filtered = filtered.filter((user) => {
        const value = user[filter.column as keyof User];
        return value === filter.value;
      });
    });
  }

  // Apply sort
  if (params.sort && params.sort.column) {
    filtered.sort((a, b) => {
      const aVal = a[params.sort!.column as keyof User];
      const bVal = b[params.sort!.column as keyof User];
      const cmp = String(aVal).localeCompare(String(bVal));
      return params.sort!.direction === "asc" ? cmp : -cmp;
    });
  }

  // Apply pagination
  const total = filtered.length;
  const start = (params.page - 1) * params.pageSize;
  const data = filtered.slice(start, start + params.pageSize);

  return { data, total, page: params.page, pageSize: params.pageSize };
}

// ============================================================================
// Main Component
// ============================================================================

const Table02 = ({
  className,
  onFetchData,
  onDelete,
  onEdit,
  onView,
}: Table02Props) => {
  const [data, setData] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState<Filter[]>([]);
  const [sort, setSort] = useState<SortState>({ column: "createdAt", direction: "desc" });
  const [pagination, setPagination] = useState<PaginationState>({
    page: 1,
    pageSize: 10,
    total: 0,
  });
  const [selectedIds, setSelectedIds] = useState<string[]>([]);

  const fetchData = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const params: FetchTableDataParams = {
        page: pagination.page,
        pageSize: pagination.pageSize,
        sort,
        filters,
        search,
      };

      const result = onFetchData
        ? await onFetchData(params)
        : await fetchMockData(params);

      setData(result.data);
      setPagination((prev) => ({ ...prev, total: result.total }));
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to fetch data");
      toast.error("Failed to load data");
    } finally {
      setLoading(false);
    }
  }, [pagination.page, pagination.pageSize, sort, filters, search, onFetchData]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // Reset to page 1 when filters/search change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [search, filters]);

  const handleDelete = async (ids: string[]) => {
    try {
      if (onDelete) {
        await onDelete(ids);
      } else {
        // Demo mode
        await new Promise((resolve) => setTimeout(resolve, 500));
      }
      toast.success(`Deleted ${ids.length} item(s)`);
      setSelectedIds([]);
      fetchData();
    } catch (err) {
      toast.error("Failed to delete");
    }
  };

  const handlePageChange = (page: number) => {
    setPagination((prev) => ({ ...prev, page }));
    setSelectedIds([]);
  };

  const handlePageSizeChange = (pageSize: number) => {
    setPagination((prev) => ({ ...prev, pageSize, page: 1 }));
    setSelectedIds([]);
  };

  return (
    <div className={cn("w-full bg-background", className)}>
      <Toaster />

      {/* Header */}
      <div className="border-b px-6 py-8">
        <h1 className="text-3xl font-semibold tracking-tight">Users</h1>
        <p className="mt-1 text-muted-foreground">
          Manage your users and their permissions.
        </p>
      </div>

      {/* Toolbar */}
      <div className="flex flex-col gap-4 border-b px-6 py-4 sm:flex-row sm:items-center sm:justify-between">
        <div className="flex flex-1 items-center gap-2">
          <SearchInput
            value={search}
            onChange={setSearch}
            placeholder="Search users..."
            className="w-64"
          />
          <TableFilters
            columns={columns}
            filters={filters}
            onFiltersChange={setFilters}
          />
        </div>
        <div className="flex items-center gap-2">
          <Button variant="outline" size="sm" onClick={fetchData} disabled={loading}>
            <RefreshCw className={cn("mr-2 size-4", loading && "animate-spin")} />
            Refresh
          </Button>
          <Button size="sm">
            <Plus className="mr-2 size-4" />
            Add user
          </Button>
        </div>
      </div>

      {/* Bulk Actions */}
      {selectedIds.length > 0 && (
        <div className="px-6 py-2">
          <BulkActions
            selectedCount={selectedIds.length}
            onDelete={() => handleDelete(selectedIds)}
            onExport={() => toast.info("Export not implemented in demo")}
          />
        </div>
      )}

      {/* Table */}
      <div className="px-6 py-4">
        {error ? (
          <div className="flex flex-col items-center justify-center rounded-lg border border-dashed py-12">
            <p className="text-muted-foreground">{error}</p>
            <Button variant="outline" size="sm" className="mt-4" onClick={fetchData}>
              Try again
            </Button>
          </div>
        ) : loading ? (
          <LoadingSkeleton columns={4} rows={5} />
        ) : (
          <DataTable
            data={data}
            columns={columns}
            sort={sort}
            onSortChange={setSort}
            selectedIds={selectedIds}
            onSelectionChange={setSelectedIds}
            onView={onView}
            onEdit={onEdit}
            onDelete={handleDelete}
          />
        )}
      </div>

      {/* Pagination */}
      {!loading && !error && (
        <div className="border-t px-6 py-4">
          <TablePagination
            pagination={pagination}
            onPageChange={handlePageChange}
            onPageSizeChange={handlePageSizeChange}
          />
        </div>
      )}
    </div>
  );
};

export { Table02 };
export type { Table02Props, User, ColumnDef, FetchTableDataParams, TableDataResult };

// ============================================================================
// Types for Data-Driven Table Component
// ============================================================================

export type SortDirection = "asc" | "desc";

export interface SortState {
  column: string;
  direction: SortDirection;
}

export interface PaginationState {
  page: number;
  pageSize: number;
  total: number;
}

export interface FilterOperator {
  value: "eq" | "neq" | "gt" | "gte" | "lt" | "lte" | "like" | "ilike" | "in";
  label: string;
}

export interface Filter {
  column: string;
  operator: FilterOperator["value"];
  value: string | number | string[];
}

export interface ColumnDef<T> {
  id: string;
  header: string;
  accessorKey: keyof T;
  sortable?: boolean;
  filterable?: boolean;
  filterType?: "text" | "select" | "date" | "number";
  filterOptions?: { label: string; value: string }[];
  cell?: (value: T[keyof T], row: T) => React.ReactNode;
  className?: string;
}

export interface TableDataResult<T> {
  data: T[];
  total: number;
  page: number;
  pageSize: number;
}

export interface FetchTableDataParams {
  page: number;
  pageSize: number;
  sort?: SortState;
  filters?: Filter[];
  search?: string;
}

// User type for demo data
export type UserStatus = "active" | "inactive" | "pending" | "suspended";
export type UserRole = "admin" | "user" | "editor" | "viewer";

export interface User {
  id: string;
  email: string;
  name: string;
  status: UserStatus;
  role: UserRole;
  createdAt: string;
  lastLogin?: string;
}

export interface Table02Props {
  className?: string;
  // Data fetching - injected by stack-specific hooks/actions
  onFetchData?: (params: FetchTableDataParams) => Promise<TableDataResult<User>>;
  // CRUD operations
  onDelete?: (ids: string[]) => Promise<void>;
  onEdit?: (id: string) => void;
  onView?: (id: string) => void;
}

export { BulkActions } from "./bulk-actions";
export { ColumnHeader } from "./column-header";
export { DataTable } from "./data-table";
export { TableFilters } from "./filters";
export { LoadingSkeleton } from "./loading-skeleton";
export { TablePagination } from "./pagination";
export { RowActions } from "./row-actions";
export { SearchInput } from "./search-input";

"use client";

import { cn } from "@/lib/utils";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { ColumnHeader } from "./column-header";
import { RowActions } from "./row-actions";
import type { ColumnDef, User, SortState } from "../types/table";

interface DataTableProps {
  data: User[];
  columns: ColumnDef<User>[];
  sort?: SortState;
  onSortChange?: (sort: SortState) => void;
  selectedIds: string[];
  onSelectionChange: (ids: string[]) => void;
  onView?: (id: string) => void;
  onEdit?: (id: string) => void;
  onDelete?: (ids: string[]) => void;
  className?: string;
}

export function DataTable({
  data,
  columns,
  sort,
  onSortChange,
  selectedIds,
  onSelectionChange,
  onView,
  onEdit,
  onDelete,
  className,
}: DataTableProps) {
  const allSelected = data.length > 0 && selectedIds.length === data.length;
  const someSelected = selectedIds.length > 0 && selectedIds.length < data.length;

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      onSelectionChange(data.map((row) => row.id));
    } else {
      onSelectionChange([]);
    }
  };

  const handleSelectRow = (id: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedIds, id]);
    } else {
      onSelectionChange(selectedIds.filter((selectedId) => selectedId !== id));
    }
  };

  const handleSort = (columnId: string) => {
    if (!onSortChange) return;

    if (sort?.column === columnId) {
      if (sort.direction === "asc") {
        onSortChange({ column: columnId, direction: "desc" });
      } else {
        onSortChange({ column: "", direction: "asc" }); // Clear sort
      }
    } else {
      onSortChange({ column: columnId, direction: "asc" });
    }
  };

  const getSortDirection = (columnId: string) => {
    if (sort?.column === columnId) {
      return sort.direction;
    }
    return false;
  };

  return (
    <div className={cn("rounded-md border", className)}>
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead className="w-12">
              <Checkbox
                checked={allSelected}
                ref={(ref) => {
                  if (ref) {
                    (ref as HTMLButtonElement).dataset.state = someSelected
                      ? "indeterminate"
                      : allSelected
                      ? "checked"
                      : "unchecked";
                  }
                }}
                onCheckedChange={handleSelectAll}
                aria-label="Select all"
              />
            </TableHead>
            {columns.map((column) => (
              <TableHead key={column.id} className={column.className}>
                <ColumnHeader
                  title={column.header}
                  sortable={column.sortable}
                  sorted={getSortDirection(column.id)}
                  onSort={() => handleSort(column.id)}
                />
              </TableHead>
            ))}
            <TableHead className="w-12" />
          </TableRow>
        </TableHeader>
        <TableBody>
          {data.length === 0 ? (
            <TableRow>
              <TableCell
                colSpan={columns.length + 2}
                className="h-24 text-center text-muted-foreground"
              >
                No results found.
              </TableCell>
            </TableRow>
          ) : (
            data.map((row) => (
              <TableRow
                key={row.id}
                data-state={selectedIds.includes(row.id) ? "selected" : undefined}
                className="hover:bg-muted/50"
              >
                <TableCell>
                  <Checkbox
                    checked={selectedIds.includes(row.id)}
                    onCheckedChange={(checked) =>
                      handleSelectRow(row.id, checked as boolean)
                    }
                    aria-label={`Select row ${row.id}`}
                  />
                </TableCell>
                {columns.map((column) => (
                  <TableCell key={column.id} className={column.className}>
                    {column.cell
                      ? column.cell(row[column.accessorKey], row)
                      : String(row[column.accessorKey] ?? "")}
                  </TableCell>
                ))}
                <TableCell>
                  <RowActions
                    onView={onView ? () => onView(row.id) : undefined}
                    onEdit={onEdit ? () => onEdit(row.id) : undefined}
                    onDelete={onDelete ? () => onDelete([row.id]) : undefined}
                  />
                </TableCell>
              </TableRow>
            ))
          )}
        </TableBody>
      </Table>
    </div>
  );
}

"use client";

import { ChevronLeft, ChevronRight, ChevronsLeft, ChevronsRight } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import type { PaginationState } from "../types/table";

interface TablePaginationProps {
  pagination: PaginationState;
  onPageChange: (page: number) => void;
  onPageSizeChange: (pageSize: number) => void;
  pageSizeOptions?: number[];
  className?: string;
}

export function TablePagination({
  pagination,
  onPageChange,
  onPageSizeChange,
  pageSizeOptions = [10, 20, 50, 100],
  className,
}: TablePaginationProps) {
  const { page, pageSize, total } = pagination;
  const totalPages = Math.ceil(total / pageSize);
  const startItem = (page - 1) * pageSize + 1;
  const endItem = Math.min(page * pageSize, total);

  const canGoPrevious = page > 1;
  const canGoNext = page < totalPages;

  return (
    <div
      className={cn(
        "flex flex-col items-center justify-between gap-4 px-2 sm:flex-row",
        className
      )}
    >
      {/* Results info */}
      <div className="text-sm text-muted-foreground">
        Showing {startItem} to {endItem} of {total} results
      </div>

      {/* Controls */}
      <div className="flex items-center gap-4">
        {/* Page size selector */}
        <div className="flex items-center gap-2">
          <span className="text-sm text-muted-foreground">Rows per page</span>
          <Select
            value={pageSize.toString()}
            onValueChange={(value) => onPageSizeChange(Number(value))}
          >
            <SelectTrigger className="h-8 w-[70px]">
              <SelectValue placeholder={pageSize.toString()} />
            </SelectTrigger>
            <SelectContent>
              {pageSizeOptions.map((size) => (
                <SelectItem key={size} value={size.toString()}>
                  {size}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* Page info */}
        <div className="text-sm text-muted-foreground">
          Page {page} of {totalPages}
        </div>

        {/* Navigation buttons */}
        <div className="flex items-center gap-1">
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => onPageChange(1)}
            disabled={!canGoPrevious}
          >
            <ChevronsLeft className="size-4" />
            <span className="sr-only">First page</span>
          </Button>
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => onPageChange(page - 1)}
            disabled={!canGoPrevious}
          >
            <ChevronLeft className="size-4" />
            <span className="sr-only">Previous page</span>
          </Button>
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => onPageChange(page + 1)}
            disabled={!canGoNext}
          >
            <ChevronRight className="size-4" />
            <span className="sr-only">Next page</span>
          </Button>
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => onPageChange(totalPages)}
            disabled={!canGoNext}
          >
            <ChevronsRight className="size-4" />
            <span className="sr-only">Last page</span>
          </Button>
        </div>
      </div>
    </div>
  );
}

"use client";

import { ArrowDown, ArrowUp, ArrowUpDown } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import type { SortDirection } from "../types/table";

interface ColumnHeaderProps {
  title: string;
  sortable?: boolean;
  sorted?: SortDirection | false;
  onSort?: () => void;
  className?: string;
}

export function ColumnHeader({
  title,
  sortable,
  sorted,
  onSort,
  className,
}: ColumnHeaderProps) {
  if (!sortable) {
    return <span className={cn("text-muted-foreground", className)}>{title}</span>;
  }

  return (
    <Button
      variant="ghost"
      size="sm"
      className={cn("-ml-3 h-8 font-medium", className)}
      onClick={onSort}
    >
      {title}
      {sorted === "asc" ? (
        <ArrowUp className="ml-1 size-3" />
      ) : sorted === "desc" ? (
        <ArrowDown className="ml-1 size-3" />
      ) : (
        <ArrowUpDown className="ml-1 size-3 text-muted-foreground" />
      )}
    </Button>
  );
}

"use client";

import { Filter, X } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import type { Filter as FilterType, ColumnDef, User } from "../types/table";

interface TableFiltersProps {
  columns: ColumnDef<User>[];
  filters: FilterType[];
  onFiltersChange: (filters: FilterType[]) => void;
  className?: string;
}

export function TableFilters({
  columns,
  filters,
  onFiltersChange,
  className,
}: TableFiltersProps) {
  const filterableColumns = columns.filter((col) => col.filterable);
  const activeFilters = filters.length;

  const handleAddFilter = (column: string, value: string) => {
    const existingIndex = filters.findIndex((f) => f.column === column);
    if (existingIndex >= 0) {
      const newFilters = [...filters];
      newFilters[existingIndex] = { column, operator: "eq", value };
      onFiltersChange(newFilters);
    } else {
      onFiltersChange([...filters, { column, operator: "eq", value }]);
    }
  };

  const handleRemoveFilter = (column: string) => {
    onFiltersChange(filters.filter((f) => f.column !== column));
  };

  const handleClearAll = () => {
    onFiltersChange([]);
  };

  const getFilterValue = (column: string) => {
    const filter = filters.find((f) => f.column === column);
    return filter?.value as string | undefined;
  };

  return (
    <div className={cn("flex items-center gap-2", className)}>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button variant="outline" size="sm" className="h-9">
            <Filter className="mr-2 size-4" />
            Filters
            {activeFilters > 0 && (
              <Badge variant="secondary" className="ml-2 rounded-full px-1.5">
                {activeFilters}
              </Badge>
            )}
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent className="w-72" align="start">
          <div className="flex items-center justify-between px-2 py-1.5">
            <DropdownMenuLabel className="p-0">Filters</DropdownMenuLabel>
            {activeFilters > 0 && (
              <Button
                variant="ghost"
                size="sm"
                className="h-auto p-0 text-xs text-muted-foreground"
                onClick={handleClearAll}
              >
                Clear all
              </Button>
            )}
          </div>
          <DropdownMenuSeparator />
          <div className="space-y-3 p-2">
            {filterableColumns.map((column) => (
              <div key={column.id} className="space-y-1.5">
                <label className="text-xs font-medium">{column.header}</label>
                <Select
                  value={getFilterValue(column.id) || ""}
                  onValueChange={(value) => {
                    if (value && value !== "_all") {
                      handleAddFilter(column.id, value);
                    } else {
                      handleRemoveFilter(column.id);
                    }
                  }}
                >
                  <SelectTrigger className="h-8 text-sm">
                    <SelectValue placeholder={`Select ${column.header.toLowerCase()}`} />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="_all">All</SelectItem>
                    {column.filterOptions?.map((option) => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            ))}
          </div>
        </DropdownMenuContent>
      </DropdownMenu>

      {/* Active filter badges */}
      {filters.map((filter) => {
        const column = columns.find((c) => c.id === filter.column);
        const option = column?.filterOptions?.find((o) => o.value === filter.value);
        return (
          <Badge key={filter.column} variant="secondary" className="gap-1">
            {column?.header}: {option?.label || filter.value}
            <button
              onClick={() => handleRemoveFilter(filter.column)}
              className="ml-1 rounded-full hover:bg-muted"
            >
              <X className="size-3" />
              <span className="sr-only">Remove filter</span>
            </button>
          </Badge>
        );
      })}
    </div>
  );
}

