import { Linkedin, Twitter } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";

interface TeamMember {
  name: string;
  role: string;
  image: string;
  socials?: {
    linkedin?: string;
    twitter?: string;
  };
}

interface Team01Props {
  tagline?: string;
  heading?: string;
  description?: string;
  buttonText?: string;
  buttonUrl?: string;
  members?: TeamMember[];
  className?: string;
}

const defaultMembers: TeamMember[] = [
  {
    name: "Ethan Brooks",
    role: "CEO, Co-founder",
    image: "https://images.unsplash.com/photo-1560250097-0b93528c311a?w=500&h=600&fit=crop&crop=face",
    socials: {
      linkedin: "#",
      twitter: "#",
    },
  },
  {
    name: "Alex Warren",
    role: "CTO, Co-founder",
    image: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=500&h=600&fit=crop&crop=face",
    socials: {
      linkedin: "#",
      twitter: "#",
    },
  },
  {
    name: "Lucy Aniston",
    role: "CFO, Co-founder",
    image: "https://images.unsplash.com/photo-1573496359142-b8d87734a5a2?w=500&h=600&fit=crop&crop=face",
    socials: {
      linkedin: "#",
      twitter: "#",
    },
  },
];

const TeamMemberCard = ({ name, role, image, socials }: TeamMember) => {
  return (
    <div className="group relative overflow-hidden rounded-xl">
      <img
        src={image}
        alt={name}
        className="aspect-[3/4] w-full object-cover transition-transform duration-300 group-hover:scale-105"
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
      <div className="absolute bottom-0 left-0 right-0 p-4 flex items-end justify-between">
        <div>
          <h3 className="text-lg font-semibold text-white">{name}</h3>
          <p className="text-sm text-white/80">{role}</p>
        </div>
        {socials && (
          <div className="flex gap-2">
            {socials.linkedin && (
              <a
                href={socials.linkedin}
                className="flex size-9 items-center justify-center rounded-full bg-white/20 text-white backdrop-blur-sm transition-colors hover:bg-white/30"
                target="_blank"
                rel="noopener noreferrer"
              >
                <Linkedin className="size-4" />
              </a>
            )}
            {socials.twitter && (
              <a
                href={socials.twitter}
                className="flex size-9 items-center justify-center rounded-full bg-white/20 text-white backdrop-blur-sm transition-colors hover:bg-white/30"
                target="_blank"
                rel="noopener noreferrer"
              >
                <Twitter className="size-4" />
              </a>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

const Team01 = ({
  tagline = "Our team",
  heading = "Meet the Team",
  description = "The people behind Acme AI are builders, designers, and problem-solvers dedicated to helping you work smarter every day.",
  buttonText = "Get in touch",
  buttonUrl = "#",
  members = defaultMembers,
  className,
}: Team01Props) => {
  return (
    <section className={cn("container mx-auto px-6 py-12 md:py-24", className)}>
      <div className="mx-auto max-w-2xl text-center">
        <p className="text-sm font-medium">{tagline}</p>
        <h2 className="mt-2 text-4xl font-semibold tracking-tight md:text-5xl">
          {heading}
        </h2>
        <p className="mt-4 text-muted-foreground">{description}</p>
        <Button variant="outline" className="mt-6" asChild>
          <a href={buttonUrl}>{buttonText}</a>
        </Button>
      </div>
      <div className="mt-12 grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {members.map((member, index) => (
          <TeamMemberCard key={index} {...member} />
        ))}
      </div>
    </section>
  );
};

export { Team01 };
