import { cn } from "@/lib/utils";
import { Card, CardContent } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Star } from "lucide-react";

interface Testimonial {
  type: "text" | "image";
  image?: string;
  title?: string;
  content?: string;
  author?: {
    name: string;
    avatar?: string;
  };
  rating?: number;
}

interface CTACard {
  title: string;
  description: string;
  buttonText: string;
  href?: string;
}

interface Testimonial02Props {
  tagline?: string;
  heading?: string;
  description?: string;
  testimonials?: Testimonial[];
  ctaCard?: CTACard;
  className?: string;
}

const StarRating = ({ rating = 5 }: { rating?: number }) => (
  <div className="flex gap-0.5">
    {Array.from({ length: rating }).map((_, i) => (
      <Star key={i} className="size-3.5 fill-yellow-400 text-yellow-400" />
    ))}
  </div>
);

const QuoteIcon = () => (
  <span className="text-2xl font-serif text-muted-foreground leading-none">"</span>
);

const TestimonialCard = ({ testimonial }: { testimonial: Testimonial }) => {
  if (testimonial.type === "image") {
    return (
      <Card className="overflow-hidden border-0 shadow-none">
        <div className="aspect-[3/4] w-full overflow-hidden rounded-xl bg-muted">
          {testimonial.image && (
            <img src={testimonial.image} alt="" className="size-full object-cover" />
          )}
        </div>
      </Card>
    );
  }

  return (
    <Card className="h-full">
      <CardContent className="flex h-full flex-col p-6">
        <QuoteIcon />
        {testimonial.title && (
          <h3 className="mt-2 text-lg font-semibold leading-snug">{testimonial.title}</h3>
        )}
        {testimonial.content && (
          <p className="mt-3 flex-1 text-sm text-muted-foreground">{testimonial.content}</p>
        )}
        {testimonial.author && (
          <div className="mt-4 flex items-center gap-3">
            <Avatar className="size-8">
              <AvatarImage src={testimonial.author.avatar} alt={testimonial.author.name} />
              <AvatarFallback>{testimonial.author.name.split(" ").map((n) => n[0]).join("")}</AvatarFallback>
            </Avatar>
            <div className="flex flex-col gap-1">
              <StarRating rating={testimonial.rating} />
              <span className="text-sm font-medium">{testimonial.author.name}</span>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

const CTACardComponent = ({ cta }: { cta: CTACard }) => (
  <Card className="bg-primary text-primary-foreground">
    <CardContent className="flex h-full flex-col p-6">
      <h3 className="text-xl font-semibold">{cta.title}</h3>
      <p className="mt-2 flex-1 text-sm opacity-90">{cta.description}</p>
      <Button variant="secondary" className="mt-4 w-full" asChild>
        <a href={cta.href || "#"}>{cta.buttonText}</a>
      </Button>
    </CardContent>
  </Card>
);

const Testimonial02 = ({
  tagline = "Testimonials",
  heading = "Wall of Love",
  description = "See what our customers are saying about their experience with our product.",
  testimonials = [
    { type: "image", image: "https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=400&h=600&fit=crop" },
    { type: "text", title: "Game Changer for Our Team!", content: "This product has completely transformed how we work. The intuitive design and powerful features save us hours every week.", author: { name: "Emma Thompson" }, rating: 5 },
    { type: "image", image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop" },
    { type: "text", title: "Incredible Support!", content: "The customer support team is exceptional. They helped us get set up quickly and answered all our questions.", author: { name: "Emily Johnson" }, rating: 5 },
    { type: "text", title: "Streamlined Our Workflow!", content: "From day one, this tool has made our processes smoother. A game-changer for productivity.", author: { name: "Alex Rodriguez" }, rating: 5 },
    { type: "image", image: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=400&h=600&fit=crop" },
    { type: "text", title: "Best Decision We Made!", content: "Switching to this platform was the best decision for our business. Highly recommend!", author: { name: "Ryan Carter" }, rating: 5 },
    { type: "image", image: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=400&h=600&fit=crop" },
    { type: "image", image: "https://images.unsplash.com/photo-1534528741775-53994a69daeb?w=400&h=600&fit=crop" },
    { type: "text", title: "Exceeded Expectations!", content: "We expected good, but got amazing. The features are exactly what we needed.", author: { name: "Jasmine Lee" }, rating: 5 },
    { type: "image", image: "https://images.unsplash.com/photo-1517841905240-472988babdf9?w=400&h=600&fit=crop" },
    { type: "text", title: "Couldn't Be Happier!", content: "Our team loves it. Easy to use, reliable, and keeps getting better with updates.", author: { name: "Christina Evans" }, rating: 5 },
  ],
  ctaCard = {
    title: "Join Our Community",
    description: "Join thousands of happy customers who have transformed their workflow with our product.",
    buttonText: "Get Started",
  },
  className,
}: Testimonial02Props) => {
  return (
    <section className={cn("container mx-auto px-6 py-12 md:py-24", className)}>
      <div className="mx-auto max-w-2xl text-center">
        <p className="text-sm font-medium">{tagline}</p>
        <h2 className="mt-2 text-4xl font-semibold tracking-tight md:text-5xl">{heading}</h2>
        <p className="mt-4 text-muted-foreground">{description}</p>
      </div>
      <div className="mt-12 columns-1 gap-4 sm:columns-2 lg:columns-4">
        {ctaCard && (
          <div className="mb-4 break-inside-avoid">
            <CTACardComponent cta={ctaCard} />
          </div>
        )}
        {testimonials.map((testimonial, index) => (
          <div key={index} className="mb-4 break-inside-avoid">
            <TestimonialCard testimonial={testimonial} />
          </div>
        ))}
      </div>
    </section>
  );
};

export { Testimonial02 };
