"use client";

import { useState, useRef } from "react";
import { cn } from "@/lib/utils";
import { Play, Pause, ArrowUpRight } from "lucide-react";

interface Video01Props {
  tagline?: string;
  heading?: string;
  description?: string;
  videoSrc?: string;
  thumbnailSrc?: string;
  thumbnailAlt?: string;
  /** Overlay text on the video (top) */
  overlayTitle?: string;
  /** Overlay text on the video (bottom) */
  overlayDescription?: string;
  /** Show arrow button in corner */
  showArrowButton?: boolean;
  /** Link for arrow button */
  arrowButtonHref?: string;
  /** Aspect ratio */
  aspectRatio?: "video" | "wide" | "square";
  className?: string;
}

const Video01 = ({
  tagline = "See it in action",
  heading = "A demo is worth a thousand words",
  description = "Watch how our platform transforms the way teams work together. No complex setup, just results.",
  videoSrc = "https://www.w3schools.com/html/mov_bbb.mp4",
  thumbnailSrc = "https://images.unsplash.com/photo-1551434678-e076c223a692?w=1200&h=675&fit=crop",
  thumbnailAlt = "Video thumbnail",
  overlayTitle,
  overlayDescription,
  showArrowButton = false,
  arrowButtonHref = "#",
  aspectRatio = "video",
  className,
}: Video01Props) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const videoRef = useRef<HTMLVideoElement>(null);

  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleVideoClick = () => {
    togglePlay();
  };

  const handleVideoEnd = () => {
    setIsPlaying(false);
    setShowControls(true);
  };

  const aspectRatioClass = {
    video: "aspect-video",
    wide: "aspect-[21/9]",
    square: "aspect-square",
  }[aspectRatio];

  return (
    <section className={cn("container mx-auto px-6 py-12 md:py-24", className)}>
      {/* Header */}
      <div className="mx-auto max-w-2xl text-center mb-12 md:mb-16">
        {tagline && (
          <p className="text-sm font-medium text-muted-foreground">{tagline}</p>
        )}
        <h2 className="mt-2 text-3xl font-semibold tracking-tight md:text-4xl lg:text-5xl">
          {heading}
        </h2>
        {description && (
          <p className="mt-4 text-muted-foreground">{description}</p>
        )}
      </div>

      {/* Video Player */}
      <div className="mx-auto max-w-5xl">
        <div
          className={cn(
            "relative group rounded-2xl overflow-hidden bg-black shadow-2xl cursor-pointer",
            aspectRatioClass
          )}
          onClick={handleVideoClick}
          onMouseEnter={() => setShowControls(true)}
          onMouseLeave={() => isPlaying && setShowControls(false)}
        >
          {/* Video */}
          <video
            ref={videoRef}
            src={videoSrc}
            poster={thumbnailSrc}
            className="size-full object-cover"
            onEnded={handleVideoEnd}
            playsInline
          />

          {/* Gradient Overlay (shown when paused or has overlay text) */}
          {(!isPlaying || overlayTitle || overlayDescription) && (
            <div
              className={cn(
                "absolute inset-0 transition-opacity duration-300",
                isPlaying && !overlayTitle && !overlayDescription
                  ? "opacity-0"
                  : "opacity-100",
                "bg-gradient-to-t from-black/60 via-transparent to-black/30"
              )}
            />
          )}

          {/* Overlay Text - Top */}
          {overlayTitle && (
            <div className="absolute top-6 left-6 right-6">
              <p className="text-white text-lg md:text-xl font-medium">
                {overlayTitle}
              </p>
            </div>
          )}

          {/* Overlay Text - Bottom */}
          {overlayDescription && (
            <div className="absolute bottom-6 left-6 right-16">
              <p className="text-white/90 text-sm md:text-base">
                {overlayDescription}
              </p>
            </div>
          )}

          {/* Play/Pause Button */}
          <div
            className={cn(
              "absolute inset-0 flex items-center justify-center transition-opacity duration-300",
              isPlaying && !showControls ? "opacity-0" : "opacity-100"
            )}
          >
            <button
              onClick={(e) => {
                e.stopPropagation();
                togglePlay();
              }}
              className={cn(
                "flex items-center justify-center rounded-full transition-all duration-300",
                "bg-white/95 hover:bg-white hover:scale-110",
                "shadow-lg backdrop-blur-sm",
                isPlaying ? "size-14" : "size-16 md:size-20"
              )}
              aria-label={isPlaying ? "Pause video" : "Play video"}
            >
              {isPlaying ? (
                <Pause className="size-6 md:size-7 text-black fill-black" />
              ) : (
                <Play className="size-7 md:size-8 text-black fill-black ml-1" />
              )}
            </button>
          </div>

          {/* Arrow Button (optional) */}
          {showArrowButton && (
            <a
              href={arrowButtonHref}
              onClick={(e) => e.stopPropagation()}
              className="absolute bottom-4 right-4 size-10 md:size-12 flex items-center justify-center rounded-full bg-white/20 hover:bg-white/30 backdrop-blur-sm transition-all duration-300 hover:scale-110"
              aria-label="Learn more"
            >
              <ArrowUpRight className="size-5 md:size-6 text-white" />
            </a>
          )}

          {/* Duration Badge (decorative) */}
          {!isPlaying && (
            <div className="absolute bottom-4 left-4 px-2 py-1 rounded bg-black/60 backdrop-blur-sm">
              <span className="text-xs text-white font-medium">2:45</span>
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

// Variant with overlay text (like image 2)
const Video01WithOverlay = (props: Omit<Video01Props, "overlayTitle" | "overlayDescription" | "showArrowButton">) => (
  <Video01
    {...props}
    overlayTitle="Quality candidates, faster"
    overlayDescription="Match with expert recruiters and fill your hardest roles in record time."
    showArrowButton={true}
    tagline=""
    heading=""
    description=""
  />
);

// Minimal variant (just the video, no header)
const Video01Minimal = (props: Omit<Video01Props, "tagline" | "heading" | "description">) => (
  <Video01
    {...props}
    tagline=""
    heading=""
    description=""
  />
);

export { Video01, Video01WithOverlay, Video01Minimal };
